import { MemberOnlineService } from "./member-online.service";
import tableMixin from '@/mixins/table.mixin';
import { columns } from "./member-online.config.ts";
import infiniteScroll from 'vue-infinite-scroll';
import { debounce } from 'lodash-es';
import ShopReceptionEntrance from '@/views/biz-modals/shop/reception/entrance';
export default {
  mixins: [tableMixin],
  directives: {
    infiniteScroll: infiniteScroll
  },
  name: 'shop-reception-member-online',
  bem: {
    b: 'shop-reception-member-online'
  },
  modals: {
    ShopReceptionEntrance: ShopReceptionEntrance
  },
  serviceInject: function serviceInject() {
    return {
      memberOnlineService: MemberOnlineService
    };
  },
  rxState: function rxState() {
    return {
      auth: this.memberOnlineService.auth$,
      list: this.memberOnlineService.list$,
      page: this.memberOnlineService.page$,
      loading: this.memberOnlineService.loading$,
      finished: this.memberOnlineService.finished$
    };
  },
  components: {},
  props: {},
  data: function data() {
    return {
      isEdit: false,
      entranceKeys: [],
      // 入场会员key值组合
      checkAll: false,
      // 是否全选
      indeterminate: false
    };
  },
  computed: {
    columns: columns,
    hasNoMember: function hasNoMember() {
      return this.page.total_counts;
    }
  },
  async: {},
  mounted: function mounted() {},
  methods: {
    handleOpenEntrance: function handleOpenEntrance(record) {
      var _this = this;

      if (this.isEdit) return false;
      this.$modalRouter.push({
        name: 'shop-reception-entrance',
        props: {
          member_id: record.member_id
        },
        on: {
          done: function done() {
            // this.$router.reload()
            _this.$emit('refresh');
          }
        }
      });
    },
    onCheckAllChange: function onCheckAllChange(e) {
      var entranceKeys = this.list.map(function (item) {
        return item.member_id;
      });
      this.entranceKeys = e.target.checked ? entranceKeys : [];
      this.indeterminate = false;
      this.checkAll = e.target.checked;
    },
    onChange: function onChange(checkedList) {
      var listLen = this.list.length;
      var checkLen = checkedList.length;
      this.indeterminate = !!checkLen && checkLen < listLen;
      this.checkAll = checkLen === listLen;
    },
    handleInfiniteOnLoad: debounce(function (e) {
      if (!this.finished) {
        this.memberOnlineService.getList({
          current_page: ++this.page.current_page,
          size: 20
        }).subscribe();
      }
    }, 400),
    // 离场之前的确认
    onLeaveConfirm: function onLeaveConfirm(record) {
      var _this2 = this;

      this.memberOnlineService.getCabinetInfo(record.member_id).subscribe(function (res) {
        if (res.info.is_smart) {
          _this2.$error({
            title: "\u5F53\u524D\u4F1A\u5458\u6709\u50A8\u7269\u67DC\u300C".concat(res.info.cabinet_area_name, "\u300D\u300C").concat(res.info.serial_num, "\u300D\u5C1A\u672A\u5F52\u8FD8\uFF0C\u8BF7\u5148\u5F52\u8FD8\uFF01"),
            okText: '知道了'
          });
        } else {
          _this2.onLeave(record);
        }
      });
    },
    onLeaveBatch: function onLeaveBatch() {
      var _this3 = this;

      this.$confirm({
        title: '批量离场',
        content: "\u786E\u5B9A\u79BB\u573A\u5417\uFF1F",
        onOk: function onOk() {
          return _this3.memberOnlineService.setEntranceLeaveBatch({
            ids: _this3.entranceKeys
          }).toPromise().then(function () {
            _this3.handleEditLeaveStatus(false);

            _this3.$emit('refresh');
          });
        }
      });
    },
    handleEditLeaveStatus: function handleEditLeaveStatus(status) {
      this.checkAll = false;
      this.isEdit = status;
      this.entranceKeys = [];
    }
  },
  beforeRouteUpdate: function beforeRouteUpdate(to, from, next) {
    console.log('member-online');
    next();
  }
};
