import StaffDetailBasics from "./add#/add-detail-basicsInfo";
import { MessageService } from '@/services/message.service';
import { AddService } from "./add.service";
export default {
  serviceInject: function serviceInject() {
    return {
      messageService: MessageService,
      addService: AddService
    };
  },
  rxState: function rxState() {
    return {
      codeList: this.addService.codeList$,
      roleList: this.addService.roleList$
    };
  },
  name: 'addDetail',
  components: {
    StaffDetailBasics: StaffDetailBasics
  },
  bem: {
    b: 'page-add-staff',
    bstep: 'page-add-staff-steps',
    bHeader: 'default-brand-header'
  },
  data: function data() {
    return {
      stepsSpan: 12,
      currentIndex: 0,
      stepArr: [{
        title: '基础信息',
        key: 1
      }, {
        title: '详细信息',
        key: 2
      }]
    };
  },
  methods: {
    // 删除步骤轴
    deletStep: function deletStep(e) {
      var _this = this;

      this.stepsSpan = 12;
      var index = this.stepArr.findIndex(function (value, index, arr) {
        return value.title === "".concat(_this.$c('coach'), "\u4FE1\u606F");
      });
      if (index === -1) return;
      this.stepArr.pop();
    },
    // 新增步骤轴
    addCoachInfo: function addCoachInfo(e) {
      this.stepsSpan = 18;
      this.stepArr.push({
        title: "".concat(this.$c('coach'), "\u4FE1\u606F"),
        key: 3
      });
    },
    goNext: function goNext(e) {
      // 切换提交信息
      var currentIndex = this.currentIndex;
      console.log(currentIndex);
      this.currentIndex = currentIndex + 1;

      if (this.currentIndex === 3) {
        this.currentIndex = 0;
      }
    }
  }
};
