import Steps from "./add#/st-steps";
import StaffDetailBasics from "./add#/add-detail-basicsInfo";
import { AddService } from "./add.service";
import { UserService } from '@/services/user.service';
import { MessageService } from '@/services/message.service';
export default {
  name: 'addDetail',
  components: {
    Steps: Steps,
    StaffDetailBasics: StaffDetailBasics
  },
  serviceInject: function serviceInject() {
    return {
      addService: AddService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      codeList: this.addService.codeList$
    };
  },
  bem: {
    b: 'page-add-staff',
    bstep: 'page-add-staff-steps',
    bHeader: 'default-brand-header'
  },
  data: function data() {
    return {
      currentIndex: 0,
      stepsSpan: 12,
      stepArr: [{
        title: '基础信息',
        key: 1
      }, {
        title: '详细信息',
        key: 2
      }],
      staingData: {}
    };
  },
  methods: {
    // 删除步骤轴
    deletStep: function deletStep(e) {
      var _this = this;

      this.stepsSpan = 12;
      var index = this.stepArr.findIndex(function (ele) {
        return ele.title === "".concat(_this.$c('coach'), "\u4FE1\u606F");
      });
      if (index === -1) return;
      this.stepArr.pop();
    },
    // 新增步骤轴
    addCoachInfo: function addCoachInfo(e) {
      this.stepsSpan = 18;
      this.stepArr.push({
        title: "".concat(this.$c('coach'), "\u4FE1\u606F"),
        key: 3
      });
    }
  }
};
