import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import { H5WrapperService } from '@/views/pages/brand/setting/mina/components#/h5/h5-wrapper.service';
import { cloneDeep, find as _find, values } from 'lodash-es';
import draggable from 'vuedraggable';
import { ActivityService } from "../activity.service";
import overImg from '@/assets/img/brand/setting/mina/over.png';
import { Tree } from '@/utils/tree';
export default {
  bem: {
    slider: 'activity-slider-component'
  },
  components: {
    draggable: draggable,
    StImageUpload: StImageUpload
  },
  serviceInject: function serviceInject() {
    return {
      h5WrapperService: H5WrapperService,
      activityService: ActivityService
    };
  },
  rxState: function rxState() {
    return {
      sliderInfo: this.h5WrapperService.sliderInfo$,
      activityList: this.activityService.actList$
    };
  },
  data: function data() {
    return {
      info: {},
      list: [],
      selected: [],
      filelist: [],
      actList: [],
      activity_id: '',
      addItem: {
        image_url: '',
        activity_id: [],
        activity_type: '',
        activity_name: '',
        is_default: 0,
        is_over: 0
      },
      actFilterList: [],
      overImg: overImg
    };
  },
  mounted: function mounted() {
    var list = cloneDeep(this.sliderInfo);
    var actList = cloneDeep(this.activityList.list);
    list.forEach(function (item, index) {
      item.activity_id = [item.activity_type, item.activity_id];
      var tree = new Tree(actList);

      if (index !== 0 && !tree.findNodeById(item.activity_id[1])) {
        var node = actList.filter(function (act) {
          return act.id === item.activity_type;
        })[0] || {};
        var tmpArrChild = {
          activity_name: item.activity_name,
          activity_type: item.activity_type,
          id: item.activity_id[1],
          isover: true
        };
        var tmpProduct = {
          product_type: item.product_type,
          product_template_id: item.product_template_id
        };

        if (node.children) {
          item.activity_type === 5 ? node.children.push(Object.assign(tmpArrChild, tmpProduct)) : node.children.push(tmpArrChild);
        }
      }
    });
    actList.forEach(function (item) {
      if (!item.children.length) {
        item.disabled = true;
      }
    });
    this.actList = cloneDeep(actList);
    this.list = cloneDeep(list);
    console.log(actList, '这是actList');
  },
  watch: {
    list: {
      deep: true,
      handler: function handler(newVal) {
        this.h5WrapperService.SET_H5INFO(newVal, 1);
      }
    }
  },
  methods: {
    filterActList: function filterActList(id) {
      return !!_find(this.list, function (o) {
        return o.activity_id[0] === id;
      });
    },
    imageUploadChange: function imageUploadChange(img) {
      var addItem = Object.assign({}, this.addItem);

      if (img.length) {
        addItem.image_key = img[0].image_key;
        addItem.image_url = img[0].image_url;
      }

      if (addItem.image_url) {
        this.list.push(addItem);
        this.addItem.activity_id = [];
        this.addItem.activity_type = '';
        this.addItem.activity_name = '';
      }
    },
    delSlider: function delSlider(index) {
      this.list.splice(index, 1);
    },
    onActSelect: function onActSelect(item, value) {
      var selected = {};
      var selecttedParent = this.actList.filter(function (it) {
        return it.id === value[0];
      })[0];
      selected = selecttedParent.children.filter(function (it) {
        return it.id === value[1];
      })[0];
      item.activity_type = selected.activity_type;
      item.activity_name = selected.activity_name;

      if (item.activity_type === 5) {
        item.product_type = selected.product_type || -1;
        item.product_template_id = selected.product_template_id || -1;
      }

      item.is_over = 0;
    },
    onAddSelect: function onAddSelect(value) {
      var selected = {};
      var selecttedParent = this.actList.filter(function (it) {
        return it.id === value[0];
      })[0];
      selected = selecttedParent.children.filter(function (it) {
        return it.id === value[1];
      })[0];
      this.addItem.activity_type = selected.activity_type;
      this.addItem.activity_name = selected.activity_name;

      if (item.activity_type === 5) {
        this.addItem.product_type = selected.product_type || -1;
        this.addItem.product_template_id = selected.product_template_id || -1;
      }
    }
  }
};
