import { MessageService } from '@/services/message.service';
import { ShelvesService } from "./shelves.service";
import { columns, CARD_TYPE } from "./shelves.config.ts";
import tableMixin from '@/mixins/table.mixin';
import CardShopMemberShelf from '@/views/biz-modals/card/shop-member/shelf';
import CardShopMemberShopTable from '@/views/biz-modals/card/shop-member/shop-table';
import { ADMISSION_RANGE } from '@/constants/card/member';
import CardSpecs from '@/views/biz-components/card/specs';
export default {
  mixins: [tableMixin],
  name: 'PageShopProductMemberShelves',
  bem: {
    shelves: 'page-shop-product-member-list-shelves'
  },
  components: {
    CardSpecs: CardSpecs
  },
  modals: {
    CardShopMemberShelf: CardShopMemberShelf,
    CardShopMemberShopTable: CardShopMemberShopTable
  },
  serviceInject: function serviceInject() {
    return {
      messageService: MessageService,
      shelvesService: ShelvesService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.shelvesService.loading$,
      publishChannel: this.shelvesService.publishChannel$,
      cardType: this.shelvesService.cardType$,
      list: this.shelvesService.list$,
      page: this.shelvesService.page$
    };
  },
  computed: {
    columns: columns
  },
  data: function data() {
    return {
      CARD_TYPE: CARD_TYPE,
      ADMISSION_RANGE: ADMISSION_RANGE
    };
  },
  methods: {
    /**
     * 查看详情
     */
    goToCardInfo: function goToCardInfo(record) {
      this.$router.push({
        path: "/shop/product/card/member/".concat(CARD_TYPE[record.card_type.id], "/info"),
        query: {
          id: record.id
        }
      });
    },
    // 下架
    onShelfDown: function onShelfDown(record) {
      var _this = this;

      this.$confirm({
        title: "\u4E0B\u67B6".concat(this.$c('member_card')),
        content: "\u786E\u5B9A\u4E0B\u67B6".concat(record.card_name).concat(this.$c('member_card'), "\u5417\uFF1F"),
        onOk: function onOk() {
          return _this.shelvesService.setCardShelfDown(record.id).toPromise().then(function () {
            _this.messageService.success({
              content: '下架成功'
            });

            _this.$router.reload();
          });
        }
      });
    },

    /**
     * 变更上架
     */
    onUpdateShelf: function onUpdateShelf(record) {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'card-shop-member-shelf',
        props: {
          title: '变更上架信息',
          id: record.id,
          actionType: 'updateShelf'
        },
        on: {
          success: function success() {
            _this2.$router.reload();
          }
        }
      });
    }
  }
};
