import formDate from "./sold#/form-date.vue";
import { SoldService } from "./sold.service";
import { classrecord } from "./sold.config.ts";
import tableMixin from '@/mixins/table.mixin';
import ScheduleSmallCourseReservedCourse from '@/views/biz-modals/schedule/small-course/reserved-course';
import ScheduleSmallCourseRemedialCourse from '@/views/biz-modals/schedule/small-course/remedial-course';
import ScheduleSmallCourseRemedialInfo from '@/views/biz-modals/schedule/small-course/remedial-info';
export default {
  mixins: [tableMixin],
  serviceInject: function serviceInject() {
    return {
      soldService: SoldService
    };
  },
  rxState: function rxState() {
    return {
      soldListInfo: this.soldService.soldListInfo$,
      soldPage: this.soldService.soldPage$,
      auth: this.soldService.auth$
    };
  },
  modals: {
    ScheduleSmallCourseReservedCourse: ScheduleSmallCourseReservedCourse,
    ScheduleSmallCourseRemedialCourse: ScheduleSmallCourseRemedialCourse,
    ScheduleSmallCourseRemedialInfo: ScheduleSmallCourseRemedialInfo
  },
  computed: {
    classrecord: classrecord
  },
  components: {
    formDate: formDate
  },
  data: function data() {
    return {
      form: {
        start_date: '',
        // 上课日期
        reserve_type: 1,
        reserve_status: -1,
        checkin_status: -1,
        course_name: undefined,
        size: 20,
        page: 1
      }
    };
  },
  created: function created() {// let self = this
    // this.soldService.init(self.$searchQuery.id, self.form).subscribe()
  },
  methods: {
    /* 取消预约 */
    reserveStatus: function reserveStatus(record) {
      var self = this;
      this.$confirm({
        title: '提示',
        content: '确认取消预约并退还相应费用？',
        okText: '确认取消',
        cancelText: '再看看',
        onOk: function onOk() {
          var getdata = {
            id: self.$searchQuery.id,
            reserve_type: record.reserve_type.id,
            reserve_id: record.id
          };
          self.soldService.getMemberCancel(getdata).subscribe(function (res) {
            self.soldService.init(self.$searchQuery.id, self.form).subscribe();
          });
        },
        onCancel: function onCancel() {}
      });
    },

    /* 课程签到 */
    isCheckin: function isCheckin(record) {
      var self = this;
      this.$confirm({
        title: '提示',
        content: '确认签到?',
        onOk: function onOk() {
          var getdata = {
            id: self.$searchQuery.id,
            reserve_type: record.reserve_type.id,
            reserve_id: record.id
          };
          console.log(record, getdata);
          self.soldService.getMemberSign(getdata).subscribe(function (res) {
            self.soldService.init(self.$searchQuery.id, self.form).subscribe();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    handleChange: function handleChange(value) {
      console.log("selected ".concat(value));
    },
    // 上课记录分页
    reservePageChange: function reservePageChange(e) {
      this.form.size = e.pageSize;
      this.form.page = e.current;
    },
    // 入场记录分页
    entrancePageChange: function entrancePageChange(e) {
      this.soldService.getMemberEntrance(this.$searchQuery.id, {
        size: e.pageSize,
        page: e.current
      }).subscribe();
    },
    // 查看补课
    getMessage: function getMessage(id) {
      this.$modalRouter.push({
        name: 'schedule-small-course-remedial-info',
        props: {
          id: id
        }
      });
      this.show = false;
    },
    // 补签到
    checkSign: function checkSign(id) {
      this.soldService.checkSign(id).subscribe(this.getReserveInfo);
    },
    // 取消预约
    del: function del(id) {
      this.soldService.del(id).subscribe(this.getReserveInfo);
    },
    // 请假
    leave: function leave(id) {
      this.soldService.leave(id).subscribe(this.getReserveInfo);
    },
    // 补课回显
    remedialCourse: function remedialCourse(reserve_id) {
      var _this = this;

      this.$modalRouter.push({
        name: 'schedule-small-course-remedial-course',
        props: {
          reserve_id: reserve_id
        },
        on: {
          ok: function ok() {
            _this.$router.reload();
          }
        }
      });
      this.show = false;
    },
    getReserveInfo: function getReserveInfo() {
      this.$router.reload();
    }
  },
  watch: {
    form: {
      handler: function handler() {
        this.soldService.getMemberReserve(this.$searchQuery.id, this.form).subscribe();
      },
      deep: true
    }
  }
};
