export default {
  name: 'StSwitchWeek',
  bem: {
    b: 'st-switch-week'
  },
  props: {
    startDate: {
      type: String
    },
    limitStartDate: {
      type: String,
      default: moment().subtract(moment().format('E') - 1, 'days').format('YYYY-MM-DD')
    },
    limitWeeks: {
      type: Number,
      default: 1
    }
  },
  data: function data() {
    return {
      startTime: '',
      rangeTime: ''
    };
  },
  created: function created() {
    var weekOfday = moment().format('E'); // 今天是本周第几天

    var mondayTime = moment().subtract(weekOfday - 1, 'days').format('YYYY-MM-DD'); // 周一日期

    this.startTime = this.startDate ? this.startDate : mondayTime;
  },
  computed: {
    startDisabled: function startDisabled() {
      var preStarDate = moment(this.startTime).subtract(7, 'days').format('YYYY-MM-DD');
      return moment(preStarDate) < moment(this.limitStartDate);
    },
    endDisabled: function endDisabled() {
      var nextStartDate = moment(this.startTime).add(7, 'days').format('YYYY-MM-DD');
      var nextLimitStartDate = moment(this.limitStartDate).add((this.limitWeeks - 1) * 7, 'days').format('YYYY-MM-DD');
      return moment(nextStartDate) > moment(nextLimitStartDate);
    },
    endTime: function endTime() {
      return moment(this.startTime).add(6, 'days').format('YYYY-MM-DD');
    }
  },
  watch: {
    startTime: function startTime() {
      var start = moment(this.startTime).format('YYYY/MM/DD');
      var end = moment(this.endTime).format('YYYY/MM/DD');
      this.rangeTime = "".concat(start, "~").concat(end);
    }
  },
  methods: {
    onClickPre: function onClickPre() {
      this.startTime = moment(this.startTime).subtract(7, 'days').format('YYYY-MM-DD');
      this.$emit('pre', {
        start_date: this.startTime,
        end_date: this.endTime
      });
    },
    onClickNext: function onClickNext() {
      this.startTime = moment(this.startTime).add(7, 'days').format('YYYY-MM-DD');
      this.$emit('next', {
        start_date: this.startTime,
        end_date: this.endTime
      });
    }
  }
};
