      type: String,
      default: ''
    }
  },
  bem: {
    bModal: 'modal-contract-constitution'
  },
  data: function data() {
    return {
      show: true,
      content: ''
    };
  },
  serviceInject: function serviceInject() {
    return {
      constitutionService: ConstitutionService,
      messageService: MessageService
    };
  },
