  },
  methods: {
    onSubmit: function onSubmit() {
      var _this = this;

      this.constitutionService.updateConstitution({
        id: this.id,
        brand_law_content: this.content
      }).subscribe(function () {
        _this.messageService.success({
          content: '更新合同章程成功！'
        });

        _this.show = false;

        _this.$emit('done');
      });
    }
  }
};
