import { courseColums } from "./columns.config";
import { CourseService } from "./course.service";
import tableMixin from '@/mixins/table.mixin';
import SchedulePersonalReserveInfo from '@/views/biz-modals/schedule/personal/reserve-info';
import SchedulePersonalTeamReserveInfo from '@/views/biz-modals/schedule/personal-team/reserve-info';
import ScheduleTeamReserveInfo from '@/views/biz-modals/schedule/team/reserve-info';
export default {
  mixins: [tableMixin],
  modals: {
    SchedulePersonalReserveInfo: SchedulePersonalReserveInfo,
    SchedulePersonalTeamReserveInfo: SchedulePersonalTeamReserveInfo,
    ScheduleTeamReserveInfo: ScheduleTeamReserveInfo
  },
  serviceInject: function serviceInject() {
    return {
      service: CourseService
    };
  },
  rxState: function rxState() {
    return {
      courseInfo: this.service.courseInfo$,
      loading: this.service.loading$,
      page: this.service.page$,
      reserveStatus: this.service.reserveStatus$
    };
  },
  data: function data() {
    return {
      id: ''
    };
  },
  computed: {
    courseColums: courseColums
  },
  mounted: function mounted() {
    this.id = this.$searchQuery.id;
  },
  methods: {
    goCourseDetai: function goCourseDetai(e) {
      console.log('跳转到课程详情', e);
      var course_type = e.course_type.id;
      var course_id = e.course_id;

      if (course_type === 3) {
        this.$router.push({
          name: 'shop-product-course-manage-team-info',
          query: {
            courseId: course_id
          }
        });
      } else {
        this.$router.push({
          name: 'shop-product-course-manage-personal-info',
          query: {
            id: course_id
          }
        });
      }
    },
    // 查看详情 点击弹出预约详情弹窗，同【门店-课程排期-团体课】、【门店-课程排期-私教1v1】、【门店-课程排期-私教小团课】
    onSearchDetail: function onSearchDetail(e) {
      var course_type = e.course_type.id;

      switch (course_type) {
        case 3:
          this.$modalRouter.push({
            name: 'schedule-team-reserve-info',
            props: {
              id: e.id
            },
            on: {
              ok: function ok(res) {
                console.log(res);
              }
            }
          });
          break;

        case 1:
          this.$modalRouter.push({
            name: 'schedule-personal-reserve-info',
            props: {
              id: String(e.id)
            },
            on: {
              ok: function ok(res) {
                console.log(res);
              }
            }
          });
          break;

        case 2:
          this.$modalRouter.push({
            name: 'schedule-personal-team-reserve-info',
            props: {
              id: e.id
            },
            on: {
              ok: function ok(res) {
                console.log(res);
              }
            }
          });
          break;
      }
    },
    // 日期选择
    onChooseDate: function onChooseDate(e) {
      console.log(e);
      this.$router.push({
        query: {
          id: this.id,
          start_time: moment(e[0]).format('YYYY-MM-DD'),
          end_time: moment(e[1]).format('YYYY-MM-DD')
        }
      });
    }
  }
};
