import { TransferService } from "./transfer.service";
import { UserService } from '@/services/user.service';
import { cloneDeep } from 'lodash-es';
import { PatternService } from '@/services/pattern.service';
import { ruleOptions } from "./transfer.config";
import EditableContractNumber from '@/views/biz-components/contract/editable-contract-number.vue';
import MemberSearch from '@/views/biz-components/member-search/member-search';
import { PERSON_TYPE } from '@/constants/course/small-course';
import moment from 'moment';
export default {
  name: 'ModalSoldCardTransfer',
  components: {
    EditableContractNumber: EditableContractNumber,
    MemberSearch: MemberSearch
  },
  bem: {
    transfer: 'modal-sold-card-transfer'
  },
  serviceProviders: function serviceProviders() {
    return [TransferService];
  },
  serviceInject: function serviceInject() {
    return {
      transferService: TransferService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.transferService.loading$,
      depositTransferInfo: this.transferService.depositTransferInfo$,
      memberTransferInfo: this.transferService.memberTransferInfo$,
      timeScope: this.transferService.timeScope$,
      payList: this.transferService.payList$,
      rangeType: this.transferService.rangeType$
    };
  },
  computed: {
    moment: moment,
    member_id: function member_id() {
      return this.memberTransferInfo.member_id;
    },
    isDeposit: function isDeposit() {
      return this.type === 'deposit';
    },
    isMember: function isMember() {
      return this.type === 'member';
    },
    saleRange: function saleRange() {
      return this.depositTransferInfo.sale_range || this.memberTransferInfo.sale_range || {};
    },
    isFamilyCard: function isFamilyCard() {
      return this.memberTransferInfo.card_number_type === 2;
    },
    reduceMax: function reduceMax() {
      return this.isMember ? this.memberTransferInfo.poundage : this.depositTransferInfo.poundage;
    }
  },
  props: ['id', 'type'],
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      PERSON_TYPE: PERSON_TYPE,
      form: form,
      decorators: decorators,
      show: false,
      // 转让信息
      transferData: {
        member_id: null,
        member_name: '',
        mobile: '',
        remain_price: null,
        contract_number: '',
        handling_fee: null,
        frozen_pay_type: null
      },
      endTime: moment()
    };
  },
  created: function created() {
    var _this = this;

    this.transferService.getTransferInfo(this.id, this.type).subscribe(function (res) {
      if (_this.isMember) {
        _this.endTime = moment(_this.memberTransferInfo.end_time);
      }
    });
  },
  methods: {
    onSubmit: function onSubmit() {
      var _this2 = this;

      this.form.validate().then(function (values) {
        var sold_type = _this2.isDeposit ? _this2.depositTransferInfo.contract_type : _this2.isMember ? _this2.memberTransferInfo.contract_type : '1';
        var start_time = _this2.isMember ? values.startTime.format('YYYY-MM-DD HH:mm') : null;
        /**
         * info混乱，需要重构，暂时不处理
         */

        var info = _this2.isMember ? _this2.memberTransferInfo : _this2.depositTransferInfo;

        _this2.transferService.editCardTransfer({
          start_time: start_time,
          transferee_member_id: +values.member_id,
          member_name: values.member_name,
          mobile: values.mobile ? values.mobile.phone : undefined,
          country_prefix: values.mobile ? values.mobile.code_id : undefined,
          remain_price: +values.remainPrice,
          contract_number: info.contract_number,
          pay_channel: +values.payType,
          contract_type: +sold_type,
          handling_fee_reduce: values.handling_fee_reduce,
          is_minors: values.is_minors,
          parent_name: values.parent_name,
          parent_mobile: values.parent_mobile ? values.parent_mobile.phone : undefined,
          parent_country_prefix: values.parent_mobile ? values.parent_mobile.code_id : undefined,
          parent_user_role: values.parent_user_role
        }, _this2.id, _this2.type).subscribe(function (res) {
          _this2.$emit('success');

          _this2.show = false;
        });
      });
    },
    // time
    disabledStartDate: function disabledStartDate(startValue) {
      return startValue.valueOf() < moment().subtract(1, 'd').valueOf() || startValue.valueOf() > moment().add(31, 'd').valueOf();
    },
    onStartTimeChange: function onStartTimeChange(data) {
      if (this.timeScope) {
        this.endTime = cloneDeep(moment(data.valueOf() + this.timeScope));
      } else {
        this.endTime = cloneDeep(moment(data.valueOf()));
      }
    }
  }
};
