import moment from 'moment';
import { cloneDeep, remove } from 'lodash-es';
import { EditUnlimitPackageService } from "./edit-unlimit-package.service";
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import ReserveIsLimit from "./components#/reserveIsLimit";
export default {
  name: 'ShopUnlimitPackageEdit',
  serviceInject: function serviceInject() {
    return {
      editPackageService: EditUnlimitPackageService
    };
  },
  rxState: function rxState() {
    return {
      transferUnitList: this.editPackageService.transferUnitList$,
      sellTypeList: this.editPackageService.sellTypeList$,
      unitList: this.editPackageService.unitList$,
      editLoading: this.editPackageService.loading$,
      packageInfo: this.editPackageService.packageInfo$
    };
  },
  components: {
    StImageUpload: StImageUpload,
    ReserveIsLimit: ReserveIsLimit
  },
  bem: {
    add: 'page-shop-add-unlimit-package',
    basic: 'page-shop-add-basic-package'
  },
  data: function data() {
    return {
      packageData: {
        // 是否支持团体课 0为不支持 1为支持
        is_team: 0,
        // 团体课总节数
        team_times: undefined,
        // 团体课单价
        team_unit_price: undefined,
        // 是否支持私教课 0为不支持 1为支持
        is_personal: 0,
        // 私教课总节数
        personal_times: undefined,
        // 私教课单价
        personal_unit_price: undefined,
        // 售价
        price: undefined,
        // 售卖开始时间
        start_time: '',
        // 售卖截止时间
        end_time: '',
        // 有效时间值
        valid_time: undefined,
        // 有效时间单位 1:天 2:月 3:年
        valid_time_unit: 1,
        // 允许冻结天数
        frozen_days: undefined,
        // 是否可以转让: 0 不可以 1 可以
        is_allow_transfer: 0,
        // 转让费率
        transfer_rate: undefined,
        // 转让单位 1:百分比 2:元
        transfer_unit: 1,
        // 售卖方式
        sale_mode: [2],
        // 封面对象
        image: {
          image_id: null,
          image_key: ''
        },
        // 课程包介绍
        intro: '',
        // 备注
        remarks: ''
      },
      form: this.$form.createForm(this),
      start_time: null,
      end_time: null,
      endOpen: false,
      // 课程范围是否未输入过
      courseIsFirstInput: true,
      // 课程范围是否未配置
      courseIsNone: false,
      courseErrorText: '',
      // 是否未传了封面
      imageIsNone: false,
      imageErrorText: '',
      fileList: [],
      cropperModal: {},
      reserveIsLimitInfo: {}
    };
  },
  mounted: function mounted() {
    this.init();
  },
  methods: {
    onChangeReserveIsliimt: function onChangeReserveIsliimt(event) {
      console.log(event);
      this.reserveIsLimit = event.target.value;
    },
    init: function init() {
      if (!this.packageInfo.is_allow_transfer) {
        this.packageInfo.transfer_rate = undefined;
      }

      if (!this.packageInfo.is_team) {
        this.packageInfo.team_times = undefined;
        this.packageInfo.team_unit_price = undefined;
      }

      if (!this.packageInfo.is_personal) {
        this.packageInfo.personal_times = undefined;
        this.packageInfo.personal_unit_price = undefined;
      } // 初始化约课限制对象 5.22 start


      var _this$packageInfo = this.packageInfo,
          reserve_is_limit = _this$packageInfo.reserve_is_limit,
          reserve_limit_day = _this$packageInfo.reserve_limit_day,
          reserve_limit_times = _this$packageInfo.reserve_limit_times;
      this.reserveIsLimitInfo = {
        reserve_is_limit: reserve_is_limit,
        reserve_limit_day: reserve_limit_day,
        reserve_limit_times: reserve_limit_times
      }; // 初始化约课限制对象 5.22 end

      this.form.setFieldsValue({
        support_num: this.packageInfo.support_num,
        price: this.packageInfo.price,
        start_time: moment(this.packageInfo.start_time),
        end_time: moment(this.packageInfo.end_time),
        valid_time: this.packageInfo.valid_time,
        frozen_days: this.packageInfo.frozen_days,
        transfer_rate: this.packageInfo.transfer_rate
      }); // 课程范围

      this.packageData.is_team = this.packageInfo.is_team;
      this.packageData.team_times = this.packageInfo.team_times;
      this.packageData.team_unit_price = this.packageInfo.team_unit_price;
      this.packageData.is_personal = this.packageInfo.is_personal;
      this.packageData.personal_times = this.packageInfo.personal_times;
      this.packageData.personal_unit_price = this.packageInfo.personal_unit_price; // 售卖时间

      this.start_time = moment(this.packageInfo.start_time);
      this.end_time = moment(this.packageInfo.end_time); // 有效时间单位

      this.packageData.valid_time_unit = this.packageInfo.valid_time_unit; // 转让设置

      this.packageData.is_allow_transfer = this.packageInfo.is_allow_transfer;
      this.packageData.transfer_unit = this.packageInfo.transfer_unit; // 售卖方式

      this.packageData.sale_mode = this.packageInfo.sale_mode; // 封面

      this.fileList = [this.packageInfo.image];
      this.packageData.image.image_id = this.fileList[0].image_id;
      this.packageData.image.image_key = this.fileList[0].image_key;
      this.imageIsNone = false;
      this.imageErrorText = ''; // 介绍

      this.packageData.intro = this.packageInfo.intro; // 备注

      this.packageData.remarks = this.packageInfo.remarks;
    },
    // 保存
    save: function save() {
      var _this = this;

      this.course_validator();
      this.image_validator();
      this.form.validateFieldsAndScroll(function (err, values) {
        if (!err && !_this.courseIsNone && !_this.imageIsNone) {
          _this.packageData.price = values.price;
          _this.packageData.valid_time = values.valid_time;
          _this.packageData.frozen_days = values.frozen_days;
          _this.packageData.support_num = values.support_num; // 约课限制 5.22 start

          _this.packageData.reserve_is_limit = values.reserve_is_limit;
          _this.packageData.reserve_limit_day = values.reserve_limit_day;
          _this.packageData.reserve_limit_times = +values.reserve_limit_times; // 约课限制 5.22 end

          _this.packageData.transfer_rate = _this.packageData.is_allow_transfer ? +values.transfer_rate : undefined;
          _this.packageData.start_time = typeof _this.start_time === 'string' ? _this.start_time : "".concat(_this.start_time.format('YYYY-MM-DD'));
          _this.packageData.end_time = typeof _this.end_time === 'string' ? _this.end_time : "".concat(_this.end_time.format('YYYY-MM-DD'));
          _this.packageData.album_id = _this.packageInfo.album_id;
          _this.packageData.team_unit_price = +_this.packageData.team_unit_price;
          _this.packageData.personal_unit_price = +_this.packageData.personal_unit_price;

          _this.editPackageService.editPackage(_this.packageData).subscribe(function (res) {
            _this.$router.push({
              path: '/shop/product/course/manage/package/list'
            });
          });
        }
      });
    },
    // course validatorFn
    course_validator: function course_validator() {
      this.courseIsFirstInput = false;
      var teamIsOk = this.packageData.is_team ? !!this.team_total : true;
      var personalIsOk = this.packageData.is_personal ? !!this.personal_total : true;

      if (teamIsOk && personalIsOk && !(!this.packageData.is_team && !this.packageData.is_personal)) {
        // 校验通过
        this.courseIsNone = false;
        this.courseErrorText = '';
      } else {
        // 校验未通过
        this.courseIsNone = true;
        this.courseErrorText = '请输入上课范围';
      }
    },
    // image validatorFn
    image_validator: function image_validator() {
      if (this.packageData.image.image_key !== '') {
        // 校验通过
        this.imageIsNone = false;
        this.imageErrorText = '';
      } else {
        // 校验未通过
        this.imageIsNone = true;
        this.imageErrorText = '请上传封面';
      }
    },
    // checkboxChange
    teamCheckboxChange: function teamCheckboxChange(e) {
      this.packageData.is_team = +e.target.checked;
      this.courseIsFirstInput = false;
    },
    personalCheckboxChange: function personalCheckboxChange(e) {
      this.packageData.is_personal = +e.target.checked;
      this.courseIsFirstInput = false;
    },
    // 转让
    transfer: function transfer(e) {
      this.packageData.is_allow_transfer = +e.target.checked; // 重置转让费用的校验

      this.packageData.transfer_rate = undefined;
      this.form.resetFields(['transfer_rate']);
    },
    fileChange: function fileChange(data) {
      if (data.length) {
        // 上传
        this.packageData.image.image_id = data[0].image_id;
        this.packageData.image.image_key = data[0].image_key;
        this.imageIsNone = false;
        this.imageErrorText = '';
      } else {
        // 删除
        this.packageData.image.image_id = null;
        this.packageData.image.image_key = '';
        this.imageIsNone = true;
        this.imageErrorText = '请上传封面';
      }
    },
    transferUnitChange: function transferUnitChange() {
      this.packageData.transfer_rate = undefined;
      this.form.setFieldsValue({
        transfer_rate: undefined
      });
    },
    // start_time validatorFn
    start_time_validator: function start_time_validator(rule, value, callback) {
      if (!value) {
        // eslint-disable-next-line
        callback('请选择开始售卖时间');
      } else {
        // eslint-disable-next-line
        callback();
      }
    },
    // end_time validatorFn
    end_time_validator: function end_time_validator(rule, value, callback) {
      if (!value) {
        // eslint-disable-next-line
        callback('请选择结束售卖时间');
      } else {
        // eslint-disable-next-line
        callback();
      }
    },
    // 售卖时间-start
    start_time_change: function start_time_change(data) {
      this.start_time = cloneDeep(data);
    },
    handleStartOpenChange: function handleStartOpenChange(open) {
      if (!open) {
        this.endOpen = true;
      }
    },
    disabledStartDate: function disabledStartDate(startValue) {
      var endValue = this.end_time;

      if (!endValue) {
        // 结束时间未选择
        return startValue.valueOf() < moment().startOf('day').valueOf();
      }

      var start = endValue.valueOf() > moment().add(30, 'y').valueOf() ? moment(endValue).subtract(30, 'y').valueOf() : moment().startOf('day').valueOf();
      return startValue.valueOf() < start || startValue.valueOf() > moment(endValue).valueOf();
    },
    // 售卖时间-end
    end_time_change: function end_time_change(data) {
      this.end_time = cloneDeep(data);
    },
    handleEndOpenChange: function handleEndOpenChange(open) {
      this.endOpen = open;
    },
    disabledEndDate: function disabledEndDate(endValue) {
      var startValue = this.start_time;

      if (!startValue) {
        // 开始时间未选择
        return endValue.valueOf() < moment().startOf('day').valueOf();
      }

      return endValue.valueOf() >= moment(startValue).add(30, 'y').valueOf() || endValue.valueOf() < moment(startValue).valueOf() || endValue.valueOf() < moment().startOf('day').valueOf();
    },
    // moment
    moment: moment
  },
  computed: {
    // 团课小计
    team_total: function team_total() {
      if (+this.packageData.team_times && +this.packageData.team_unit_price) {
        return this.packageData.team_times * (this.packageData.team_unit_price * 10) / 10;
      } else {
        return 0;
      }
    },
    // 私教小计
    personal_total: function personal_total() {
      if (+this.packageData.personal_times && +this.packageData.personal_unit_price) {
        return this.packageData.personal_times * (this.packageData.personal_unit_price * 10) / 10;
      } else {
        return 0;
      }
    },
    // 总价
    all_total: function all_total() {
      var teamTotal = this.packageData.is_team ? this.team_total : 0;
      var personalTotal = this.packageData.is_personal ? this.personal_total : 0;
      return teamTotal + personalTotal;
    }
  }
};
