import { EditService } from "../edit.service";
import { MessageService } from '@/services/message.service';
import { UserService } from '@/services/user.service';
import { ruleOptions } from "../form.config";
import { PatternService } from '@/services/pattern.service';
import CardBgRadio from '@/views/biz-components/card-bg-radio/card-bg-radio';
import StEditor from '@/views/biz-components/st/editor/editor.vue';
import { CLASS_STATUS } from '@/constants/course/small-course';
export default {
  name: 'create-group-course',
  serviceInject: function serviceInject() {
    return {
      editService: EditService,
      messageService: MessageService,
      userService: UserService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.editService.loading$,
      rangeList: this.editService.rangeList$,
      isAllowLeave: this.editService.isAllowLeave$
    };
  },
  props: {
    info: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  bem: {
    b: 'create-group-course'
  },
  components: {
    CardBgRadio: CardBgRadio,
    StEditor: StEditor
  },
  computed: {
    isDisabled: function isDisabled() {
      return this.CLASS_STATUS.SIGNING_UNCLASSED === this.info.info.class_status || this.CLASS_STATUS.SIGNING_CLASSED === this.info.info.class_status || this.CLASS_STATUS.CLASSED === this.info.info.class_status;
    }
  },
  mounted: function mounted() {
    this.isShowLimitContent = this.$route.query.type === '1';
    this.$emit('onCourseNameChange', this.info.info.course_name);
    this.$emit('onCourseIdChange', this.info.info.course_id);
  },
  data: function data(vm) {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      bg_image: {
        image_id: 0,
        image_key: '',
        image_url: '',
        index: 1
      },
      isShowLeaveContent: false,
      isShowLimitContent: false,
      CLASS_STATUS: CLASS_STATUS
    };
  },
  methods: {
    onInitEditor: function onInitEditor() {
      this.$nextTick(this.setFieldsValue());
    },
    setFieldsValue: function setFieldsValue() {
      var info = this.info.info;
      this.form.setFieldsValue({
        course_name: info.course_name,
        scope_application: info.scope_application,
        date: info.course_begin_time ? [moment(info.course_begin_time), moment(info.course_end_time)] : [],
        num_min: info.num_min,
        num_max: info.num_max,
        course_times: info.course_times,
        is_leave: this.isShowLimitContent ? info.is_leave : undefined,
        leave_hours: this.isShowLimitContent ? info.leave_hours : undefined,
        leave_limit: this.isShowLimitContent ? info.leave_limit : undefined,
        appointment_rights: !this.isShowLimitContent ? info.appointment_rights : undefined,
        image: info.image
      });
      this.bg_image.index = info.img_type;

      if (info.img_type === 3) {
        this.bg_image = info.image;
        this.bg_image.index = 0;
      }

      this.isShowLeaveContent = this.isShowLimitContent && info.is_leave === 1;
    },
    save: function save(e) {
      var _this = this;

      e.preventDefault();
      this.form.validate().then(function (values) {
        values.id = _this.info.info.course_id;
        values.course_begin_time = values.date[0].format('YYYY-MM-DD');
        values.course_end_time = values.date[1].format('YYYY-MM-DD');
        values.small_course_type = _this.$route.query.type;
        values.image = _this.bg_image;
        values.img_type = _this.bg_image.index;

        if (!_this.isShowLeaveContent) {
          delete values.leave_hours;
          delete values.leave_limit;
        }

        if (_this.bg_image.index === 0) {
          values.img_type = 3;

          if (!_this.bg_image.image_url) {
            _this.messageService.error({
              content: '请上传图片'
            });

            return;
          }
        }

        delete values.date;

        _this.editService.editGroup(values).subscribe(function (res) {
          _this.messageService.success({
            content: '提交成功'
          });

          _this.$emit('goNext', res.course_id);
        });
      });
    },
    onLimitChange: function onLimitChange(e) {
      this.isShowLeaveContent = e.target.value === 1;
    },
    onCourseTypeChange: function onCourseTypeChange(category_id) {
      this.form.setFieldsValue({
        category_id: category_id
      });
    },
    onCourseNameChange: function onCourseNameChange(e) {
      this.$emit('onCourseNameChange', e.target.value);
    },
    disabledDate: function disabledDate(current) {
      return current && current.format('YYYY-MM-DD') < moment().add(1, 'day').format('YYYY-MM-DD');
    }
  }
};
