export default {
  name: 'courseManageService',
  props: {
    teamTemplateList: {
      type: Array,
      default: function _default() {
        return [];
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      show: false,
      idArr: []
    };
  },
  methods: {
    // 新增课表
    addCourse: function addCourse() {
      this.$emit('add');
      this.show = false;
    },
    // 编辑课表
    editRow: function editRow(id) {
      this.$emit('edit', id);
      this.show = false;
    },
    // 点击删除，暂存删除id
    delRow: function delRow(id, index) {
      this.teamTemplateList.splice(index, 1);
      this.idArr.push(id);
    },
    // 确定删除
    save: function save() {
      this.$emit('save', this.idArr);
      this.show = false;
    }
  }
};
