export default {
  name: 'StMinaPanel',
  props: {
    initial: {
      type: Boolean,
      default: false
    },
    responsivePreview: {
      type: Boolean,
      default: false // 窗口缩放是否显示

    }
  },
  bem: {
    b: 'st-mina-panel'
  },
  data: function data() {
    return {
      isActionFixed: true,
      actionLeft: 0,
      actionRight: 0,
      footerEl: null
    };
  },
  methods: {
    handleActionsPosition: function handleActionsPosition() {
      var rect = this.footerEl.getBoundingClientRect();
      var minaMain = document.querySelector('.st-mina-panel__main').getBoundingClientRect(); // 视窗内

      if (rect.top < window.innerHeight) {
        this.isActionFixed = false;
      } else {
        this.isActionFixed = true;
        this.actionLeft = minaMain.left;
      }
    }
  },
  computed: {
    isShowPreview: function isShowPreview() {
      return this.$slots.preview;
    },
    actionStyle: function actionStyle() {
      if (!this.isActionFixed) {
        return {};
      }

      return {
        left: this.actionLeft + 'px'
      };
    }
  },
  mounted: function mounted() {
    this.footerEl = document.querySelector('.layout-default-body__footer');

    if (this.footerEl) {
      this.handleActionsPosition();
      window.addEventListener('scroll', this.handleActionsPosition, false);
      window.addEventListener('resize', this.handleActionsPosition, false);
    }
  },
  beforeDestroy: function beforeDestroy() {
    window.removeEventListener('scroll', this.handleActionsPosition, false);
    window.removeEventListener('resize', this.handleActionsPosition, false);
  }
};
