import { UserService } from '@/services/user.service';
import { MessageService } from '@/services/message.service';
import { AddService } from "../add.service";
import CoachLevelSelect from '@/views/biz-components/coach-level-select';
import ShopSelect from '@/views/biz-components/shop-select';
import { SEX } from '@/constants/common/form';
import { PatternService } from '@/services/pattern.service';
import { ruleOptions } from "../staff-form.config.ts";
import DepartmentSelect from '@/views/biz-components/department-select';
import FaceUpload from '@/views/biz-components/face-upload/face-upload';
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import { cloneDeep } from 'lodash-es';
export default {
  name: 'StaffDetailBasics',
  bem: {
    addBasicsInfo: 'page-shop-staff-add-detail'
  },
  serviceInject: function serviceInject() {
    return {
      pattern: PatternService,
      userService: UserService,
      message: MessageService,
      addService: AddService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.addService.loading$,
      shop: this.userService.shop$,
      id_types: this.userService.getOptions$('staff.id_type'),
      nature_works: this.userService.getOptions$('staff.nature_work'),
      identitys: this.userService.getOptions$('staff.identity')
    };
  },
  components: {
    ShopSelect: ShopSelect,
    CoachLevelSelect: CoachLevelSelect,
    DepartmentSelect: DepartmentSelect,
    FaceUpload: FaceUpload,
    StImageUpload: StImageUpload
  },
  props: {
    roleList: {
      type: Array
    },
    codeList: {
      type: Array
    },
    department: {
      type: Array
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      SEX: SEX,
      fileList: [],
      faceList: [],
      isChoosePermission: false,
      isAdd: [],
      addflag: true,
      isShowLevel: false,
      // 是否展示教练等级
      treeExpandedKeys: [],
      value: undefined,
      id_type: 1,
      country_code_id: 37
    };
  },
  mounted: function mounted() {
    this.form.setFieldsValue({
      shop_id: this.shop.id
    });
  },
  methods: {
    resetID: function resetID() {
      this.form.setFieldsValue({
        id_number: undefined
      });
    },
    onChangeGetAvatar: function onChangeGetAvatar(imageFiles) {
      this.fileList = cloneDeep(imageFiles);
    },
    onChangeGetFace: function onChangeGetFace(imageFiles) {
      this.faceList = cloneDeep(imageFiles);
    },
    getIsCoach: function getIsCoach(data) {
      this.isShowLevel = data.includes(4);

      if (!this.isShowLevel) {
        this.$emit('deletStep');
        this.addflag = false;
      } else {
        if (this.addflag) return;
        this.$emit('addStep');
        this.addflag = true;
      }
    },
    permissionChange: function permissionChange(e) {
      this.isChoosePermission = e.target.checked;
    },
    onChange: function onChange(value) {
      console.log('选择部门', value);
      this.value = value;
    },
    onSelectIdtype: function onSelectIdtype(e) {
      console.log('证件选择', e);
    },
    // 继续填写跳转到编辑
    goNext: function goNext(e) {
      var _this = this;

      e.preventDefault();
      this.form.validate().then(function (values) {
        _this.submit(values, 1);
      });
    },

    /**
     * saveOrgoNext 0 保存 1 跳转到编辑
     */
    submit: function submit(data) {
      var _this2 = this;

      data.is_permission = +this.isChoosePermission;
      data.entry_date = moment(data.entry_date).format('YYYY-MM-DD');
      data.image_avatar = this.fileList[0] || {};
      data.image_face = this.faceList[0] || {};
      data.country_code_id = this.country_code_id;
      data.id_type = this.id_type;
      this.addService.addStaff(data).subscribe(function (res) {
        _this2.$router.push({
          name: 'shop-staff-edit',
          query: {
            id: res.staff_id,
            currentIndex: 1,
            isShowCoach: data.identity.includes(3) || data.identity.includes(4) ? 1 : 0
          }
        });
      });
    }
  }
};
