import CreatePersonalCourse from "./components#/add/create-personal-course";
import SetShopCoach from "./components#/add/set-shop-coach";
import SetSellPrice from "./components#/add/set-sell-price";
import { AddService } from "./add.service";
export default {
  components: {
    CreatePersonalCourse: CreatePersonalCourse,
    // 创建私教课程
    SetShopCoach: SetShopCoach,
    // 设置上课门店及教练
    SetSellPrice: SetSellPrice // 设置售卖价格

  },
  serviceInject: function serviceInject() {
    return {
      addService: AddService
    };
  },
  data: function data() {
    return {
      currentIndex: 0,
      courseId: 0,
      courseName: '',
      stepArr: [{
        title: '创建私教课',
        key: 0
      }, {
        title: "\u8BBE\u7F6E\u552E\u5356\u95E8\u5E97\u53CA".concat(this.$c('coach')),
        key: 1
      }, {
        title: '设置售卖价格',
        key: 2
      }]
    };
  },
  methods: {
    goNext: function goNext(courseId) {
      if (courseId) {
        console.log(courseId);
        this.courseId = courseId;
      }

      if (this.currentIndex < 2) {
        this.currentIndex++;
      }
    },
    changeStep: function changeStep(step) {
      this.currentIndex = step;
    },
    onCourseNameChange: function onCourseNameChange(courseName) {
      this.courseName = courseName;
      console.log('courseName', courseName);
    }
  }
};
