import EditCoachInfo from "./edit#/edit-coach-info";
import EditDetailedInfo from "./edit#/edit-detailed-info";
import EditBasicInfo from "./edit#/edit-basic-info";
import { UserService } from '@/services/user.service';
import { MessageService } from '@/services/message.service';
import { EditService } from "./edit.service.ts";
export default {
  bem: {
    b: 'page-add-staff',
    bstep: 'page-add-staff-steps',
    bHeader: 'default-brand-header'
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService,
      messageService: MessageService,
      services: EditService
    };
  },
  rxState: function rxState() {
    return {
      staffInfo: this.services.staffInfo$,
      staffSpecialty: this.services.staffSpecialty$
    };
  },
  components: {
    EditCoachInfo: EditCoachInfo,
    EditDetailedInfo: EditDetailedInfo,
    EditBasicInfo: EditBasicInfo
  },
  data: function data() {
    return {
      stepsSpan: 18,
      currentIndex: 0,
      stepArr: [{
        title: '基础信息',
        key: 0
      }, {
        title: '详细信息',
        key: 1
      }, {
        title: "".concat(this.$c('coach'), "\u4FE1\u606F"),
        key: 2
      }]
    };
  },
  computed: {
    isShowCoach: function isShowCoach() {
      if (this.staffInfo.identity.length === 0) return false;
      return this.staffInfo.identity.includes(3) || this.staffInfo.identity.includes(4);
    },
    isPrivateCoach: function isPrivateCoach() {
      if (this.staffInfo.identity.length === 0) return false;
      return this.staffInfo.identity.includes(4);
    },
    id: function id() {
      return this.staffInfo.staff_id;
    }
  },
  watch: {
    isPrivateCoach: function isPrivateCoach(newVal) {
      if (newVal) {
        this.stepsSpan = 12;
        this.stepArr.pop();
      }
    }
  },
  created: function created() {
    var currentIndex = this.$searchQuery.currentIndex;

    if (!this.isPrivateCoach) {
      this.stepsSpan = 12;
      this.stepArr.pop();
    }

    if (currentIndex) this.currentIndex = Number(currentIndex);
  },
  methods: {
    updateStaffInfo: function updateStaffInfo() {
      console.log('updateStaffInfo');
      this.services.getStaffInfo(this.id).subscribe();
    },
    onBack: function onBack(step) {
      this.currentIndex = this.currentIndex - step;
    },
    gonext: function gonext() {
      if (this.currentIndex < 2) {
        this.currentIndex = this.currentIndex + 1;
      }
    },
    changeStep: function changeStep(step) {
      this.currentIndex = step;
    }
  }
};
