import h5PhoneComponent from "./h5-phone.component";
import { H5SettingReserveService } from "./h5-setting-reserve.service";
import { MessageService } from '@/services/message.service';
import h5ModuleComponent from "./h5-module.component";
import h5ShopListComponent from "./h5-shop-list.component"; // 模块组件

import h5FormReserveinfoComponent from "./form-components#/h5-form.reserveinfo.component";
export default {
  name: 'H5SettingReserveComponent',
  components: {
    h5PhoneComponent: h5PhoneComponent,
    h5ModuleComponent: h5ModuleComponent,
    h5ShopListComponent: h5ShopListComponent,
    h5FormReserveinfoComponent: h5FormReserveinfoComponent
  },
  bem: {
    h5: 'h5-setting-reserve-component'
  },
  data: function data() {
    return {
      // 选择的 module
      selectModuleId: '',
      sliderIndex: 0,
      modulesDataString: [],
      shopId: -1,
      shopActiveIndex: 0,
      isEditFlag: false
    };
  },
  serviceInject: function serviceInject() {
    return {
      messageService: MessageService,
      h5SettingReserveService: H5SettingReserveService
    };
  },
  rxState: function rxState() {
    return {
      shopList: this.h5SettingReserveService.shopList$,
      modulesData: this.h5SettingReserveService.modulesData$,
      loading: this.h5SettingReserveService.loading$
    };
  },
  created: function created() {
    this.init();
  },
  watch: {
    modulesData: {
      handler: function handler(val) {
        console.log('XXXXX', val);
        this.modulesDataString = JSON.parse(JSON.stringify(val));
      },
      deep: true,
      immediate: true
    }
  },
  methods: {
    init: function init() {
      var _this = this;

      this.h5SettingReserveService.getShopList().subscribe(function (res) {
        _this.selectModuleId = res.info.module_list[0].id;
        _this.sliderIndex = 0;
        _this.shopId = res.info.shop_id;
        _this.shopActiveIndex = 0;
      });
    },
    // 切换了门店
    switchShop: function switchShop(shopId) {
      var _this2 = this;

      if (shopId != this.shopId) {
        if (this.isEditFlag) {
          this.$confirm({
            title: '确定要离开？',
            content: '此时离开将丢失已编辑的内容，是否确认离开？',
            onOk: function onOk() {
              _this2.changShopRegetHandle(shopId);

              _this2.editEmit(false);
            }
          });
        } else {
          this.changShopRegetHandle(shopId);
        }
      }
    },
    changShopRegetHandle: function changShopRegetHandle(shopId) {
      var _this3 = this;

      this.shopId = shopId;
      var param = {
        shop_id: this.shopId,
        tab_type: '2'
      };
      this.shopActiveIndex = this.shopList.findIndex(function (item) {
        return item.shop_id === _this3.shopId;
      });
      this.h5SettingReserveService.getShopModules(param).subscribe(function (res) {
        console.log(res);
      });
    },
    tapSlide: function tapSlide(_ref) {
      var selectModuleId = _ref.selectModuleId,
          sliderIndex = _ref.sliderIndex;
      this.selectModuleId = selectModuleId;
      this.sliderIndex = sliderIndex;
    },
    draggChange: function draggChange(list) {
      this.modulesDataString[0].content.list = [];
      this.modulesDataString[0].content.list = list;
      this.editEmit();
    },
    submitForm: function submitForm() {
      var _this4 = this;

      this.$confirm({
        title: '',
        content: '确认将当前设置内容发布到小程序？',
        onOk: function onOk() {
          var list = _this4.modulesDataString[0].content.list;
          var info = {
            tab_type: '2',
            shop_id: _this4.shopId,
            module_list: _this4.modulesDataString
          };
          console.log('info---', info);

          _this4.h5SettingReserveService.postShopModules({
            info: info
          }).subscribe(function (res) {
            _this4.messageService.success({
              content: '保存成功'
            });

            _this4.editEmit(false);
          });
        }
      });
    },
    editEmit: function editEmit() {
      var flag = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : true;
      this.isEditFlag = flag;
      this.$emit('change', this.isEditFlag);
    }
  }
};
