import { StopSaleService } from "./stop-sale.service";
export default {
  name: 'ModalCardBrandMemberStopSale',
  bem: {
    stopSale: 'modal-card-brand-stop-sale'
  },
  serviceInject: function serviceInject() {
    return {
      stopSaleService: StopSaleService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.stopSaleService.loading$,
      info: this.stopSaleService.info$
    };
  },
  props: ['id', 'cardType', 'cardName'],
  data: function data() {
    var form = this.$stForm.create();
    return {
      form: form,
      show: false,
      // 卡tag类型
      cardTypeTag: {
        1: 'number-card',
        2: 'period-card'
      },
      reason: '',
      // 停售原因是否有填写
      reasonIsNone: true
    };
  },
  methods: {
    onReasonChange: function onReasonChange(data) {
      this.reasonIsNone = data.trim() === '';
    },
    onStop: function onStop() {
      var _this = this;

      this.stopSaleService.setStopSale({
        reason: this.reason
      }, this.id).subscribe(function () {
        _this.show = false;

        _this.$emit('success');
      });
    }
  },
  mounted: function mounted() {
    this.stopSaleService.getStopSaleInfo(this.id).subscribe();
  }
};
