import moment from 'moment';
import { cloneDeep, filter } from 'lodash-es';
import { SmallCourseService } from "./small-course.service";
import tableMixin from '@/mixins/table.mixin';
import { columns } from "./small-course.config";
import useSmallCourseActions from '@/hooks/shop/sold-small-course-actions.hook';
import useInternalNoteActions from '@/hooks/shop/sold-internal-note-actions.hook';
export default {
  name: 'PageShopSoldCoursePersonalList',
  mixins: [tableMixin],
  bem: {
    basic: 'page-shop-sold'
  },
  serviceInject: function serviceInject() {
    return {
      smallCourseService: SmallCourseService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.smallCourseService.loading$,
      list: this.smallCourseService.list$,
      page: this.smallCourseService.page$,
      courseStatus: this.smallCourseService.courseStatus$,
      auth: this.smallCourseService.auth$
    };
  },
  beforeCreate: function beforeCreate() {
    var _this = this;

    this.smallCourseActions.$on('refresh', function (val) {
      _this.$router.reload();
    });
  },
  hooks: function hooks() {
    return {
      smallCourseActions: useSmallCourseActions(),
      internalNoteActions: useInternalNoteActions()
    };
  },
  data: function data() {
    return {
      start_time: null,
      end_time: null,
      // 结束时间面板是否显示
      endOpen: false,
      selectedRowKeys: [],
      date: []
    };
  },
  computed: {
    columns: columns
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  methods: {
    // 查询
    onSearchNative: function onSearchNative() {
      this.$searchQuery.start_time = this.date[0] ? "".concat(this.date[0].format('YYYY-MM-DD'), " 00:00:00") : '';
      this.$searchQuery.end_time = this.date[1] ? "".concat(this.date[1].format('YYYY-MM-DD'), " 23:59:59") : '';
      this.onSearch();
    },
    onSearchResetNative: function onSearchResetNative() {
      this.date = [null, null];
      this.onSearchReset();
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      var start = this.$searchQuery.start_time ? cloneDeep(moment(this.$searchQuery.start_time)) : null;
      var end = this.$searchQuery.end_time ? cloneDeep(moment(this.$searchQuery.end_time)) : null;
      this.date = [start, end];
    },
    // moment
    moment: moment,
    // 跳转合同
    toContract: function toContract(record) {
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      window.open(url);
    },
    // 详情
    onDetail: function onDetail(record) {
      this.$router.push({
        path: "/shop/sold/course/info/small-course/info/operation-record",
        query: {
          id: record.id
        }
      });
    }
  }
};
