import { cloneDeep, filter } from 'lodash-es';
import { ListService } from "./list.service";
import { columns } from "./list.config";
import tableMixin from '@/mixins/table.mixin';
import ShopFinanceCancel from '@/views/biz-modals/shop/finance/cancel';
import ShopFinanceRefund from '@/views/biz-modals/shop/finance/refund';
import ShopFinanceSplit from '@/views/biz-modals/shop/finance/split';
import SoldDealGathering from '@/views/biz-modals/sold/deal/gathering';
import SoldLeaseTransfer from '@/views/biz-modals/sold/lease/transfer';
import { ORDER_PRODUCT_TYPE } from '@/constants/finance/order';
export default {
  name: 'PageShopFinanceOrder',
  mixins: [tableMixin],
  modals: {
    ShopFinanceCancel: ShopFinanceCancel,
    ShopFinanceRefund: ShopFinanceRefund,
    ShopFinanceSplit: ShopFinanceSplit,
    SoldDealGathering: SoldDealGathering,
    SoldLeaseTransfer: SoldLeaseTransfer
  },
  serviceInject: function serviceInject() {
    return {
      listService: ListService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.listService.loading$,
      page: this.listService.page$,
      list: this.listService.list$,
      orderStatus: this.listService.orderStatus$,
      payStatus: this.listService.payStatus$,
      productTypeList: this.listService.productType$,
      auth: this.listService.auth$
    };
  },
  computed: {
    columns: columns
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  watch: {
    $searchQuery: function $searchQuery() {
      this.setSearchData();
    }
  },
  data: function data() {
    return {
      ORDER_PRODUCT_TYPE: ORDER_PRODUCT_TYPE,
      keyword: '',
      status: -1,
      type: -1,
      createDate: [null, null],
      payDate: [null, null]
    };
  },
  methods: {
    // 子订单退款
    onChildredRefund: function onChildredRefund(record) {
      var _this = this;

      var props = {
        id: record.id,
        isParent: false,
        product_type: record.product_type
      };
      this.$modalRouter.push({
        name: 'shop-finance-refund',
        props: props,
        on: {
          success: function success(result) {
            _this.$router.reload();
          }
        }
      });
    },
    // 打印小票
    printOrder: function printOrder(order_id) {
      window.open('/ticket/gathering-print?id=' + order_id, '_blank', 'width=800,height=600');
    },
    formatDate: function formatDate(date) {
      return date ? "".concat(moment(date).format('YYYY-MM-DD')) : '';
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      var searchQuery = this.$searchQuery;
      this.createDate = [searchQuery.start_date || null, searchQuery.end_date || null];
      this.payDate = [searchQuery.pay_start_date || null, searchQuery.pay_end_date || null];
    },
    // 查询
    onSearchNative: function onSearchNative() {
      var createDate = this.createDate,
          payDate = this.payDate;
      this.$searchQuery.start_date = this.formatDate(createDate[0]);
      this.$searchQuery.end_date = this.formatDate(createDate[1]);
      this.$searchQuery.pay_start_date = this.formatDate(payDate[0]);
      this.$searchQuery.pay_end_date = this.formatDate(payDate[1]);
      this.onSearch();
    },
    // 收款
    onGathering: function onGathering(record) {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'sold-deal-gathering',
        props: {
          order_id: record.id,
          type: this.productType(record.product_type)
        },
        on: {
          success: function success() {
            _this2.onSearch();
          }
        }
      });
    },
    // 取消
    onCancel: function onCancel(record) {
      var _this3 = this;

      this.$modalRouter.push({
        name: 'shop-finance-cancel',
        props: {
          id: record.id
        },
        on: {
          success: function success(result) {
            console.log('取消订单!');

            _this3.$router.reload();
          }
        }
      });
    },
    // 转让
    onTransfer: function onTransfer(record) {
      var _this4 = this;

      this.$modalRouter.push({
        name: 'sold-lease-transfer',
        props: {
          id: record.id
        },
        on: {
          success: function success(result) {
            console.log('转让成功!');

            _this4.$router.reload();
          }
        }
      });
    },
    // 退款
    onRefund: function onRefund(record) {
      var _this5 = this;

      var props = {
        id: record.id,
        isParent: true,
        product_type: record.product_type
      };
      this.$modalRouter.push({
        name: 'shop-finance-refund',
        props: props,
        on: {
          success: function success(result) {
            _this5.$router.reload();
          }
        }
      });
    },
    // 业务拆分
    onSplit: function onSplit(record) {
      var _this6 = this;

      this.$modalRouter.push({
        name: 'shop-finance-split',
        props: {
          id: record.id
        },
        on: {
          success: function success(result) {
            _this6.$router.reload();
          }
        }
      });
    },
    // 订单详情
    onOrderInfo: function onOrderInfo(record) {
      this.$router.push({
        path: '/shop/finance/order/info/collection-details',
        query: {
          id: record.id
        }
      });
    },
    productType: function productType(type) {
      var name = ''; // 1-会员卡 2-私教课 3-团体课 4-课程包 5-储值卡 6-小班课 7-手续费 8-定金 9-押金 10-储物柜 12-云店 13-场地预约

      switch (type) {
        case 1:
          name = 'member';
          break;

        case 2:
          name = 'personal';
          break;

        case 3:
          name = 'team';
          break;

        case 4:
          name = 'package';
          break;

        case 5:
          name = 'deposit';
          break;

        case 6:
          name = 'small_course_order/detail';
          break;

        case 7:
          name = 'advance';
          break;

        case 8:
          name = 'advance';
          break;

        case 9:
          name = 'member';
          break;

        case 10:
          name = 'cabinet_order';
          break;

        case 12:
          name = 'cloud_store';
          break;

        case 13:
          name = 'venues';
      }

      return name;
    },
    onProductTypeChange: function onProductTypeChange() {}
  }
};
