import tableMixin from '@/mixins/table.mixin';
import { columns } from "./list.config";
import { ListService } from "./list.service";
import { UserService } from '@/services/user.service';
export default {
  mixins: [tableMixin],
  name: 'PageShopStoreList',
  bem: {
    basic: 'page-shop-store-list'
  },
  serviceInject: function serviceInject() {
    return {
      listService: ListService
    };
  },
  rxState: function rxState() {
    return {
      list: this.listService.list$,
      page: this.listService.page$,
      loading: this.listService.loading$,
      goodsList: this.listService.goodsList$,
      cloudStore: this.listService.shelvesStatus$,
      auth: this.listService.auth$
    };
  },
  data: function data(vm) {
    return {
      goodsName: '',
      // 商品名称
      shelvesStatus: -1,
      // 上架状态
      categoryId: -1,
      // 商品分类
      columns: columns(vm)
    };
  },
  computed: {
    goods_sort: function goods_sort() {
      var list = [];
      this.goodsList.forEach(function (item) {
        list.push({
          value: +item.category_id,
          label: item.category_name
        });
      });
      return [{
        value: -1,
        label: '所有商品分类'
      }].concat(list);
    }
  },
  mounted: function mounted() {
    this.setSearchData();
    console.log(this.auth);
  },
  watch: {
    query: function query(newVal) {
      this.setSearchData();
    }
  },
  methods: {
    // 设置状态&名称
    setSearchData: function setSearchData() {
      var _this$$searchQuery = this.$searchQuery,
          product_name = _this$$searchQuery.product_name,
          shelves_status = _this$$searchQuery.shelves_status,
          category_id = _this$$searchQuery.category_id;
      this.goodsName = product_name;
      this.categoryId = category_id || -1;
      this.shelvesStatus = shelves_status || -1;
    },
    // 新建商品
    onAddGoods: function onAddGoods() {
      this.$router.push({
        path: './add'
      });
    },
    // 上下架
    onShelf: function onShelf(record) {
      var _this = this;

      var status = record.product_shelves === 1 ? 2 : 1;
      this.$confirm({
        title: '提示',
        content: "\u786E\u5B9A".concat(status === 1 ? '上架' : '下架', "\u8BE5\u5546\u54C1\u5417\uFF1F"),
        onOk: function onOk() {
          _this.listService.onShelf(record.product_id, {
            shelves_status: status
          }).subscribe(function (res) {
            _this.$router.reload();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    onDel: function onDel(record) {
      var _this2 = this;

      console.log(record);
      this.$confirm({
        title: '提示',
        content: "\u4E00\u65E6\u5220\u9664\u5219\u65E0\u6CD5\u6062\u590D\uFF0C\u786E\u5B9A\u5220\u9664".concat(record.product_name, "\uFF1F"),
        onOk: function onOk() {
          _this2.listService.delproduct(record.product_id).subscribe(function (res) {
            _this2.$router.reload();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    onEdit: function onEdit(record) {
      this.$router.push({
        path: './edit',
        query: {
          id: record.product_id
        }
      });
    }
  }
};
