import GroupForm from "./components#/group-form";
import { ruleOptions, cardColumns } from "./add-package-course.config";
import { EditPackageCourseService } from "./edit-package-course.service";
import { UserService } from '@/services/user.service';
import { ACTIVITY_STATUS, PRODUCT_TYPE } from '@/constants/marketing/group-buy';
import moment from 'moment';
import { PatternService } from '@/services/pattern.service';
export default {
  serviceInject: function serviceInject() {
    return {
      editPackageCourseService: EditPackageCourseService,
      userService: UserService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.editPackageCourseService.loading$,
      shopList: this.userService.shopList$,
      courseList: this.editPackageCourseService.courseList$,
      info: this.editPackageCourseService.info$
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.editPackageCourseService.getCourseList({
      shop_id: this.info.support_shop[0]
    }).subscribe(function (res) {
      _this.setFieldsValue();

      _this.tableData = _this.info.sku;
    });
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      cardColumns: cardColumns,
      courseList: [],
      tableData: [],
      ACTIVITY_STATUS: ACTIVITY_STATUS,
      PRODUCT_TYPE: PRODUCT_TYPE,
      tableText: '',
      // 优惠设置错误提示
      tableErr: false,
      disabledEdit: false,
      oldStock: Number
    };
  },
  methods: {
    changeShop: function changeShop(value) {
      this.editPackageCourseService.getCourseList({
        shop_id: value
      }).subscribe(function (res) {});
    },
    changeCourse: function changeCourse(value) {
      this.tableData = this.courseList.filter(function (item) {
        return item.id === value;
      })[0].product_spec;
    },
    // 处理输入拼团价格的逻辑
    setPriceChange: function setPriceChange() {
      if (!this.tableData[0].group_price) {
        this.tableText = '请输入拼团价格';
        this.tableErr = true;
      } else {
        this.tableText = '';
        this.tableErr = false;
      }
    },
    onSubmit: function onSubmit(data) {
      var _this2 = this;

      console.log(data);
      data.id = +this.$route.query.id;
      data.shop_ids = [+this.form.getFieldValue('shop_id')];
      data.product_type = PRODUCT_TYPE.PACKAGE_COURSE;
      data.product_id = this.form.getFieldValue('course_id');
      data.sku = this.tableData.map(function (item) {
        return {
          sku_id: _this2.form.getFieldValue('course_id'),
          group_price: item.group_price
        };
      });
      this.editPackageCourseService.editGroupbuy(data).subscribe(function (res) {
        _this2.$router.push({
          path: "./list"
        });
      });
    },
    // 详情回显
    setFieldsValue: function setFieldsValue() {
      console.log(this.info);
      this.oldStock = this.info.stock_total;
      this.oldTime = new Date(this.info.end_time);
      this.form.setFieldsValue({
        shop_id: this.info.support_shop[0],
        course_id: this.info.product.id
      }); // 是否能够编辑, 当活动未开始时可以编辑

      this.disabledEdit = this.info.activity_state.id >= ACTIVITY_STATUS.NO_START;
    }
  },
  components: {
    GroupForm: GroupForm
  }
};
