import { BasicService } from "./basic.service";
import { MessageService } from '@/services/message.service';
import tableMixin from '@/mixins/table.mixin';
import FinanceAddTemplate from '@/views/biz-modals/finance/add-template';
import FinanceBasicTemplateEdit from '@/views/biz-modals/finance/basic-template-edit';
import FinanceSearchStaffListSalary from '@/views/biz-modals/finance/search-staff-list-salary';
export default {
  mixins: [tableMixin],
  modals: {
    FinanceAddTemplate: FinanceAddTemplate,
    FinanceBasicTemplateEdit: FinanceBasicTemplateEdit,
    FinanceSearchStaffListSalary: FinanceSearchStaffListSalary
  },
  serviceInject: function serviceInject() {
    return {
      basicService: BasicService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      // 路由query订阅
      list: this.basicService.list$,
      loading: this.basicService.loading$,
      page: this.basicService.page$,
      auth: this.basicService.auth$
    };
  },
  data: function data() {
    return {
      columsTitlelist: [{
        title: '模板名称'
      }, {
        title: '月底薪(元)'
      }, {
        title: '应用员工'
      }, {
        title: '创建时间'
      }, {
        title: '操作'
      }]
    };
  },
  methods: {
    refresh: function refresh() {
      this.$router.push({
        query: {
          size: this.page.pageSize,
          page: this.page.current
        }
      });
    },
    onDelete: function onDelete(e) {
      console.log(e);
      var that = this;
      this.$confirm({
        title: '提示',
        content: '是否确认删除？',
        onOk: function onOk() {
          console.log('OK');
          that.basicService.deleteTemplate(e).subscribe(function () {
            console.log('ok');
            that.messageService.success({
              content: '删除成功'
            });
            that.$router.push({
              query: {}
            });
          });
        },
        onCancel: function onCancel() {
          console.log('Cancel');
        },
        class: 'test'
      });
    }
  }
};
