import { timer } from 'rxjs';
import { TodoListService } from "./todolist.service";
import FrontAddWorkNote from '@/views/biz-modals/front/add-work-note';
export default {
  name: 'shop-reception-todolist',
  bem: {
    b: 'shop-reception-todolist'
  },
  modals: {
    FrontAddWorkNote: FrontAddWorkNote
  },
  components: {},
  props: {},
  data: function data() {
    return {
      activeIndex: 0
    };
  },
  serviceInject: function serviceInject() {
    return {
      todolistService: TodoListService
    };
  },
  rxState: function rxState() {
    return {
      auth: this.todolistService.auth$,
      loading: this.todolistService.loading$,
      workNoteList: this.todolistService.workNoteList$,
      workNoteDoneList: this.todolistService.workNoteDoneList$
    };
  },
  computed: {
    notesTabTips: function notesTabTips() {
      var len = this.workNoteList.length;
      var str = '待办';

      if (len) {
        str += "(".concat(len, ")");
      }

      return str;
    }
  },
  async: {},
  mounted: function mounted() {
    this.todolistService.init().subscribe();
  },
  methods: {
    // 新增待办
    onAddWorkNotes: function onAddWorkNotes() {
      var _this = this;

      this.$modalRouter.push({
        name: 'front-add-work-note',
        on: {
          success: function success() {
            _this.todolistService.getWorkNoteList().subscribe();
          }
        }
      });
    },
    onSetWorkNote: function onSetWorkNote(item) {
      var _this2 = this;

      this.$confirm({
        title: '完成待办',
        content: "\u786E\u5B9A\u5B8C\u6210".concat(item.subject, "\u5417\uFF1F"),
        onOk: function onOk() {
          return _this2.todolistService.setWorkNote(item.id).toPromise().then(function () {
            timer(800).subscribe(function () {
              _this2.todolistService.getWorkNoteList().subscribe();

              _this2.todolistService.getWorkNoteDoneList().subscribe();
            });
          });
        }
      });
    }
  }
};
