import { CrmService } from "./crm.service";
import { MessageService } from '@/services/message.service';
import BrandSettingCrmReset from '@/views/biz-modals/brand/setting/crm/reset';
export default {
  bem: {
    bPage: 'setting-general-crm'
  },
  serviceInject: function serviceInject() {
    return {
      crmService: CrmService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      courseType: this.crmService.courseType$,
      userUntied: this.crmService.userUntied$,
      crmRule: this.crmService.crmRule$,
      loading: this.crmService.loading$,
      auth: this.crmService.auth$
    };
  },
  modals: {
    BrandSettingCrmReset: BrandSettingCrmReset
  },
  data: function data() {
    return {};
  },
  created: function created() {},
  methods: {
    setFocus: function setFocus(key, value) {
      this.crmRule[key] = value;
    },
    reset: function reset(val, type) {
      var _this = this;

      if (!val) {
        this.$modalRouter.push({
          name: 'brand-setting-crm-reset',
          props: {
            text: '关闭客保后,所有已设置的规则将会重置已分配跟进人员的客户也不再受客保限制是否继续?'
          },
          on: {
            success: function success() {
              if (type === 'sales') {
                _this.crmRule.sales_is_protect = 0;
                _this.crmRule.sales_is_protect_limit = 0;
                _this.crmRule.sales_protect_days = '';
                _this.crmRule.sales_follow_rule = 1;
                _this.crmRule.sales_follow_days = '';
              }

              if (type === 'coach') {
                _this.crmRule.coach_is_protect = 0;
                _this.crmRule.coach_is_protect_limit = 0;
                _this.crmRule.coach_protect_days = '';
                _this.crmRule.coach_follow_rule = 1;
                _this.crmRule.coach_follow_days = '';
                _this.crmRule.coach_untie_condition = [1, 2];
              }
            },
            cancel: function cancel() {
              if (type === 'sales') {
                _this.crmRule.sales_is_protect = 1;
              }

              if (type === 'coach') {
                _this.crmRule.coach_is_protect = 1;
              }
            }
          }
        });
      }
    },
    setCrmRule: function setCrmRule() {
      var _this2 = this;

      if (this.crmRule.sales_is_limit === 0) {
        delete this.crmRule.sales_limit_num;
      } else {
        if (!this.crmRule.sales_limit_num) {
          this.messageService.warn({
            content: '请输入大于0的销售可跟进客户天数'
          });
          return;
        }
      }

      if (this.crmRule.coach_is_limit === 0) {
        delete this.crmRule.coach_limit_num;
      } else {
        if (!this.crmRule.coach_limit_num) {
          this.messageService.warn({
            content: "\u8BF7\u8F93\u5165\u5927\u4E8E0\u7684".concat(this.$c('coach'), "\u53EF\u8DDF\u8FDB\u5BA2\u6237\u5929\u6570")
          });
          return;
        }
      }

      if (this.crmRule.sales_is_protect_limit === 0) {
        delete this.crmRule.sales_protect_days;
      } else {
        if (!this.crmRule.sales_protect_days) {
          this.messageService.warn({
            content: '请输入大于0的销售跟进客户保护天数'
          });
          return;
        }
      }

      if (this.crmRule.sales_follow_rule !== 3) {
        delete this.crmRule.sales_follow_days;
      } else {
        if (this.crmRule.sales_follow_rule === 3 && !this.crmRule.sales_follow_days) {
          this.messageService.warn({
            content: '请输入大于0的跟进天数'
          });
          return;
        }
      }

      if (this.crmRule.coach_is_protect_limit === 0) {
        delete this.crmRule.coach_protect_days;
      } else {
        if (!this.crmRule.coach_protect_days) {
          this.messageService.warn({
            content: "\u8BF7\u8F93\u5165\u5927\u4E8E0\u7684".concat(this.$c('coach'), "\u8DDF\u8FDB\u5BA2\u6237\u4FDD\u62A4\u5929\u6570")
          });
          return;
        }
      }

      if (this.crmRule.coach_follow_rule !== 3) {
        delete this.crmRule.coach_follow_days;
      } else {
        if (this.crmRule.coach_follow_rule === 3 && !this.crmRule.coach_follow_days) {
          this.messageService.warn({
            content: '请输入大于0的跟进天数'
          });
          return;
        }
      }

      return this.crmService.setCrmRule(this.crmRule).subscribe(function (res) {
        _this2.messageService.success({
          content: '保存成功,新规则将在明日0点生效'
        });

        _this2.$router.reload();
      });
    }
  }
};
