import { MessageService } from '@/services/message.service';
import { EditDescriptionService } from "./edit-description.service";
import { ruleOptions } from "./edit-description.config";
export default {
  props: {
    order_id: {
      type: Number,
      default: 0
    },
    description: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false
    };
  },
  serviceInject: function serviceInject() {
    return {
      messageService: MessageService,
      editDescriptionService: EditDescriptionService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.editDescriptionService.loading$
    };
  },
  mounted: function mounted() {
    this.initForm();
  },
  methods: {
    initForm: function initForm() {
      this.form.setFieldsValue({
        description: this.description
      });
    },
    onSubmit: function onSubmit() {
      var _this = this;

      this.form.validate().then(function (values) {
        _this.editDescriptionService.updateContractDescription(_this.order_id, values).subscribe(function () {
          _this.messageService.success({
            content: '修改成功'
          });

          _this.show = false;

          _this.$emit('success');
        });
      });
    }
  }
};
