import { PrizeService } from "./lottery-table.service";
import { columns } from "./lottery-table.config";
import tableMixin from '@/mixins/table.mixin';
export default {
  name: 'ModalBrandMarketingLotteryTable',
  serviceInject: function serviceInject() {
    return {
      prizeService: PrizeService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.prizeService.loading$,
      list: this.prizeService.list$,
      page: this.prizeService.page$
    };
  },
  computed: {
    columns: columns
  },
  props: ['id', 'title'],
  data: function data() {
    return {
      show: false
    };
  },
  created: function created() {
    this.getList({
      size: 20,
      current_page: 1,
      id: this.id
    });
  },
  methods: {
    getList: function getList(query) {
      this.prizeService.getPrizedList(query).subscribe();
    },
    onPageChange: function onPageChange(data) {
      this.getList({
        size: data.pageSize,
        current_page: data.current,
        id: this.id
      });
    }
  }
};
