  data: function data() {
    return {
      packageData: {
        // 课程包名称
        course_name: '',
        // 售价
        price: undefined,
        // 总节数 0为不限次 1为限次
        is_limit: 1,
        // 是否支持团体课 0为不支持 1为支持
        is_team: 0,
        // 团体课总节数
        team_times: undefined,
        // 团体课单价
        team_unit_price: undefined,
        // 团体课上课范围课程id
        team_range: [],
        // 是否支持私教课 0为不支持 1为支持
        is_personal: 0,
        // 私教课总节数
        personal_times: undefined,
        // 私教课单价
        personal_unit_price: undefined,
        // 私教课上课范围课程id course_id私教课课程id coach_level私教课支持预约的教练等级
        personal_range: [],
        // 售卖开始时间
        start_time: '',
        // 售卖截止时间
        end_time: '',
        // 有效时间值
        valid_time: undefined,
        // 有效时间单位 1:天 2:月 3:年
        valid_time_unit: 1,
        // 允许冻结天数
        frozen_days: undefined,
        // 售卖方式
        sale_mode: [2],
        // 是否可以转让: 0 不可以 1 可以
        is_allow_transfer: 0,
        // 转让费率
        transfer_rate: undefined,
        // 转让单位 1:百分比 2:元
        transfer_unit: 1,
        // 封面对象
        image: {
          image_id: null,
          image_key: ''
        },
        // 课程包介绍
        intro: '',
        // 备注
        remarks: ''
      },
      // 课程范围是否未输入过
      courseIsFirstInput: true,
      // team course
      teamCourseList: [],
      teamCourseListIsShow: false,
      teamIndeterminate: false,
      teamCheckAll: false,
      // personal course
      personalCourseList: [],
      personalCoachTotalList: {},
      personalCoachListHistory: [],
      personalAllOperationCoachList: [],
      personalAllOperationCoachTotal: 0,
      personalCourseListIsShow: false,
      personalIndeterminate: false,
      personalCheckAll: false,
      coachAllOperationDropdownIsShow: false,
      // 课程范围是否未配置
      courseIsNone: false,
      courseErrorText: '',
      form: this.$form.createForm(this),
      start_time: null,
      end_time: null,
      endOpen: false,
      // 是否配置了用户端
      appConfig: true,
      // 是否未传了封面
      imageIsNone: false,
      imageErrorText: '',
      fileList: [],
      cropperModal: {},
      reserveIsLimitOptions: [{
        label: '不限制',
        value: 0
      }, {
        label: '限制',
        value: 1
      }]
    };
  },
  methods: {
    limitChange: function limitChange(e) {
      console.log(e);
      var value = e.target.value;
      this.packageData.is_limit = +value;

      if (value) {
        this.packageData.team_times = undefined;
        this.packageData.personal_times = undefined;
        this.form.setFieldsValue({
          team_times: undefined,
          personal_times: undefined
        });
      } else {
        this.packageData.team_times = 1;
        this.packageData.personal_times = 1;
        this.form.setFieldsValue({
          team_times: 1,
          personal_times: 1
        });
      }
    },
    // 保存
    save: function save() {
      var _this = this;

      this.course_validator();
      this.image_validator();
      this.form.validateFieldsAndScroll({
        scroll: {
          offsetTop: 80
        }
      }, function (err, values) {
        if (!err && !_this.courseIsNone && !_this.imageIsNone) {
          _this.packageData.course_name = values.course_name;
          _this.packageData.price = values.price;
          _this.packageData.valid_time = values.valid_time;
          _this.packageData.support_num = values.support_num;
          _this.packageData.frozen_days = values.frozen_days;
          _this.packageData.transfer_rate = _this.packageData.is_allow_transfer ? +values.transfer_rate : undefined;
          _this.packageData.start_time = "".concat(_this.start_time.format('YYYY-MM-DD')); // 约课限制 5.22 start

          _this.packageData.reserve_is_limit = values.reserve_is_limit;
          _this.packageData.reserve_limit_day = values.reserve_limit_day;
          _this.packageData.reserve_limit_times = +values.reserve_limit_times; // 约课限制 5.22 end

          _this.packageData.end_time = "".concat(_this.end_time.format('YYYY-MM-DD'));
          _this.packageData.team_range = [];

          _this.teamCourseList.forEach(function (i) {
            _this.packageData.team_range.push(i.course_id);
          });

          _this.packageData.personal_range = [];

          _this.personalCourseList.forEach(function (i) {
            _this.packageData.personal_range.push({
              course_id: i.course_id,
              coach_level: cloneDeep(i.coachGradeList)
            });
          });

          _this.addPackageService.add(_this.packageData).subscribe(function (res) {
            _this.$router.push({
              path: '/shop/product/course/manage/package/list'
            });
          });
        }
      });
    },
    // 保存并上架
    onsale: function onsale() {
      var _this2 = this;

      this.course_validator();
      this.image_validator();
      this.form.validateFieldsAndScroll(function (err, values) {
        if (!err && !_this2.courseIsNone && !_this2.imageIsNone) {
          _this2.packageData.course_name = values.course_name;
          _this2.packageData.price = values.price;
          _this2.packageData.valid_time = values.valid_time;
          _this2.packageData.support_num = values.support_num;
          _this2.packageData.frozen_days = values.frozen_days;
          _this2.packageData.transfer_rate = _this2.packageData.is_allow_transfer ? +values.transfer_rate : undefined;
          _this2.packageData.start_time = "".concat(_this2.start_time.format('YYYY-MM-DD'));
          _this2.packageData.end_time = "".concat(_this2.end_time.format('YYYY-MM-DD')); // 约课限制 5.22 start

          _this2.packageData.reserve_is_limit = values.reserve_is_limit;
          _this2.packageData.reserve_limit_day = values.reserve_limit_day;
          _this2.packageData.reserve_limit_times = +values.reserve_limit_times; // 约课限制 5.22 end

          _this2.packageData.team_range = [];

          _this2.teamCourseList.forEach(function (i) {
            _this2.packageData.team_range.push(i.course_id);
          });

          _this2.packageData.personal_range = [];

          _this2.personalCourseList.forEach(function (i) {
            _this2.packageData.personal_range.push({
              course_id: i.course_id,
              coach_level: cloneDeep(i.coachGradeList)
            });
          });

          _this2.addPackageService.addAndOnsale(_this2.packageData).subscribe(function (res) {
            _this2.$router.push({
              path: '/shop/product/course/manage/package/list'
            });
          });
        }
      });
    },
    // checkboxChange
    teamCheckboxChange: function teamCheckboxChange(e) {
      this.packageData.is_team = +e.target.checked;
      this.courseIsFirstInput = false;
      this.teamCourseListIsShow = +e.target.checked;
    },
    personalCheckboxChange: function personalCheckboxChange(e) {
      this.packageData.is_personal = +e.target.checked;
      this.courseIsFirstInput = false;
      this.personalCourseListIsShow = +e.target.checked;
    },
    // team
    teamCourseListShow: function teamCourseListShow() {
      this.teamCourseListIsShow = !this.teamCourseListIsShow;
    },
    // personal
    personalCourseListShow: function personalCourseListShow() {
      this.personalCourseListIsShow = !this.personalCourseListIsShow;
    },
    // 新增课程
    addCourse: function addCourse(type) {
      var that = this;

      if (type === 'team') {
        this.$modalRouter.push({
          name: 'course-select-course',
          props: {
            type: type,
            use_range: 1,
            courses: this.teamCourseList
          },
          on: {
            ok: function ok(data) {
              data.list.forEach(function (i) {
                i.courseChecked = false;
              });
              that.teamCourseList = cloneDeep(data.list);
            }
          }
        });
      } else if (type === 'personal') {
        this.$modalRouter.push({
          name: 'course-select-course',
          props: {
            type: type,
            courses: this.personalCourseList
          },
          on: {
            ok: function ok(data) {
              var coachGradeList = [];
              forEach(that.coachList, function (i) {
                coachGradeList.push(i.id);
              });
              var total = reduce(that.coachList, function (sum, o) {
                return sum + o.coach_number;
              }, 0);
              data.list.forEach(function (i) {
                i.courseChecked = false;
                i.coachGradeList = coachGradeList;
                i.coach = total;
              });
              forEach(that.personalCoachTotalList, function (value, key) {
                forEach(data.list, function (innerValue) {
                  if (+innerValue.course_id === +key) {
                    innerValue.coachGradeList = cloneDeep(value.list);
                    innerValue.coach = cloneDeep(value.total);
                  }
                }); // 删除personalCoachTotalList里不存在的course_id项

                if (!data.list.some(function (o) {
                  return +o.course_id === +key;
                })) {
                  delete that.personalCoachTotalList[key];
                }
              });
              that.personalCoachListHistory = []; // 缓存选择的教练等级

              data.list.forEach(function (i) {
                that.personalCoachListHistory.push(i.coachGradeList);
              });
              that.personalCourseList = cloneDeep(data.list);
            }
          }
        });
      }
    },
    // 移除课程
    removeCourseItem: function removeCourseItem(type, id) {
      if (type === 'team') {
        var arr = cloneDeep(this.teamCourseList);
        remove(arr, function (o) {
          return o.course_id === id;
        });
        this.teamCourseList = arr;
      } else if (type === 'personal') {
        var _arr = cloneDeep(this.personalCourseList);

        remove(_arr, function (o) {
          return o.course_id === id;
        });
        this.personalCourseList = _arr; // 移除备份选择的教练list

        var coachList = cloneDeep(this.personalCoachTotalList);
        delete coachList[id];
        this.personalCoachTotalList = coachList;
      }
    },
    // 移除选择的课程
    removeCourseItems: function removeCourseItems(type) {
      if (type === 'team') {
        var arr = cloneDeep(this.teamCourseList);
        remove(arr, function (o) {
          return o.courseChecked;
        });
        this.teamCourseList = arr;
      } else if (type === 'personal') {
        var _arr2 = cloneDeep(this.personalCourseList);

        var removeList = remove(_arr2, function (o) {
          return o.courseChecked;
        });
        this.personalCourseList = _arr2; // 移除备份选择的教练list

        var coachList = cloneDeep(this.personalCoachTotalList);
        removeList.forEach(function (i) {
          delete coachList[i.course_id];
        });
        this.personalCoachTotalList = coachList;
      }
    },
    // 选择课程
    courseItemCheckedChange: function courseItemCheckedChange(e, type, index) {
      if (type === 'team') {
        this.teamCourseList[index].courseChecked = e.target.checked;
      } else if (type === 'personal') {
        this.personalCourseList[index].courseChecked = e.target.checked;
      }
    },
    // 全选
    checkAllChange: function checkAllChange(type) {
      var _this3 = this;

      if (type === 'team') {
        this.teamCourseList.forEach(function (i) {
          i.courseChecked = !_this3.teamCheckAll;
        });
      } else if (type === 'personal') {
        this.personalCourseList.forEach(function (i) {
          i.courseChecked = !_this3.personalCheckAll;
        });
      }
    },
    // item coach checkboxGroup changeEvent
    coachItemChange: function coachItemChange(e, id, index) {
      if (!e.length) {
        this.personalCourseList[index].coachGradeList = this.personalCoachListHistory[index];
        return;
      } else {
        this.personalCoachListHistory[index] = cloneDeep(e);
      }

      this.personalCoachTotalList[id] = {};
      this.personalCoachTotalList[id].list = cloneDeep(e);
      var selectCoach = filter(this.coachList, function (o) {
        return e.includes(o.id);
      });
      var total = reduce(selectCoach, function (sum, o) {
        return sum + o.coach_number;
      }, 0);
      this.personalCoachTotalList[id].total = total; // 设置教练总数

      this.personalCourseList.forEach(function (i) {
        if (i.course_id === id) {
          i.coach = total;
        }
      });
    },
    coachAllChange: function coachAllChange(e) {
      var selectCoach = filter(this.coachList, function (o) {
        return e.includes(o.id);
      });
      this.personalAllOperationCoachTotal = reduce(selectCoach, function (sum, o) {
        return sum + o.coach_number;
      }, 0);
    },
    coachAllOperationOk: function coachAllOperationOk() {
      var _this4 = this;

      // 批量设置
      forEach(this.personalCourseList, function (value) {
        if (value.courseChecked) {
          value.coachGradeList = cloneDeep(_this4.personalAllOperationCoachList);
          value.coach = _this4.personalAllOperationCoachTotal;
          _this4.personalCoachTotalList[value.course_id] = {};
          _this4.personalCoachTotalList[value.course_id].list = cloneDeep(_this4.personalAllOperationCoachList);
          _this4.personalCoachTotalList[value.course_id].total = _this4.personalAllOperationCoachTotal;
        }
      }); // 缓存选择的教练等级

      this.personalCoachListHistory = [];
      forEach(this.personalCourseList, function (i) {
        _this4.personalCoachListHistory.push(i.coachGradeList);
      }); // 清空

      this.personalAllOperationCoachList = [];
      this.personalAllOperationCoachTotal = 0; // 收起

      this.coachAllOperationDropdownIsShow = false;
    },
    // course validatorFn
    course_validator: function course_validator() {
      this.courseIsFirstInput = false;
      var teamIsOk = false;
      var personalIsOk = false;

      if (this.packageData.is_team) {
        teamIsOk = this.team_total && this.teamCourseList.length;
      } else {
        teamIsOk = true;
      }

      if (this.packageData.is_personal) {
        personalIsOk = this.personal_total && this.personalCourseList.length;
      } else {
        personalIsOk = true;
      }

      if (teamIsOk && personalIsOk && !(!this.packageData.is_team && !this.packageData.is_personal)) {
        // 校验通过
        this.courseIsNone = false;
        this.courseErrorText = '';
      } else {
        // 校验未通过
        this.courseIsNone = true;
        this.courseErrorText = '请输入上课范围';
      }
    },
    // image validatorFn
    image_validator: function image_validator() {
      if (this.packageData.image.image_key !== '') {
        // 校验通过
        this.imageIsNone = false;
        this.imageErrorText = '';
      } else {
        // 校验未通过
        this.imageIsNone = true;
        this.imageErrorText = '请上传封面';
      }
    },
    // 转让
    transfer: function transfer(e) {
      this.packageData.is_allow_transfer = +e.target.checked; // 重置转让费用的校验

      this.packageData.transfer_rate = undefined;
      this.form.resetFields(['transfer_rate']);
    },
    fileChange: function fileChange(data) {
      if (data.length) {
        // 上传
        this.packageData.image.image_id = data[0].image_id;
        this.packageData.image.image_key = data[0].image_key;
        this.imageIsNone = false;
        this.imageErrorText = '';
      } else {
        // 删除
        this.packageData.image.image_id = null;
        this.packageData.image.image_key = '';
        this.imageIsNone = true;
        this.imageErrorText = '请上传封面';
      }
    },
