import { AddService } from "../add.service";
import { MessageService } from '@/services/message.service';
import { UserService } from '@/services/user.service';
import { RuleConfig } from '@/constants/course/rule';
import SetPrice from '@/views/fragments/course/set-price';
import { GradientService } from '@/views/fragments/course/personal#/gradient.service';
export default {
  name: 'SetSellPrice',
  serviceInject: function serviceInject() {
    return {
      addService: AddService,
      messageService: MessageService,
      userService: UserService,
      ruleConfig: RuleConfig,
      gradientService: GradientService
    };
  },
  rxState: function rxState() {
    var user = this.userService;
    return {
      loading: this.addService.loading$,
      sell_types: user.getOptions$('personal_course.sell_type')
    };
  },
  props: {
    info: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  components: {
    SetPrice: SetPrice
  },
  data: function data() {
    return {
      form: this.$stForm.create(),
      priceGradient: [],
      singleReserve: 0,
      singlePrice: ''
    };
  },
  computed: {
    sellTypeOptions: function sellTypeOptions() {
      var sellType = this.sell_types;
      var options = [];

      for (var i in sellType) {
        options.push({
          label: sellType[i].label,
          value: sellType[i].value,
          disabled: sellType[i].value === 2
        });
      }

      return options;
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.$nextTick(function () {
      _this.setFieldsValue();
    });
  },
  methods: {
    save: function save(e) {
      var _this2 = this;

      e.preventDefault();
      this.form.validate().then(function () {
        var data = _this2.getData();

        if (!_this2.inputCheck()) {
          return;
        }

        _this2.addService.setPrice(data).subscribe(_this2.onSaveSuccess);
      });
    },
    onSaveSuccess: function onSaveSuccess() {
      this.messageService.success({
        content: '提交成功'
      });
      this.$router.push({
        name: 'shop-product-course-manage-personal-list'
      });
    },
    getPriceGradient: function getPriceGradient() {
      var ret = [];
      var tableData = this.tableData;
      tableData.forEach(function (item) {
        ret.push({
          level_id: item.level_id,
          min_sale: item.min_sale,
          max_sale: item.max_sale,
          price: item.price,
          transfer_num: item.transfer_num,
          transfer_unit: item.transfer_unit
        });
      });
      return ret;
    },
    inputCheck: function inputCheck() {
      if (this.singleReserve) {
        if (!this.singlePrice.length) {
          this.messageService.error({
            content: '请输入单节预约价格'
          });
          return;
        }
      }

      if (!this.gradientService.check(this.priceGradient)) {
        return false;
      }

      return true;
    },
    setFieldsValue: function setFieldsValue() {
      var info = this.info; // if (info.sell_type.indexOf(2) === -1) {
      //   info.sell_type.push(2)
      // }

      this.form.setFieldsValue({
        course_name: info.course_name,
        sell_type: info.sell_type,
        effective_unit: info.effective_unit
      });
      this.singleReserve = info.single_reserve;
      this.singlePrice = info.single_price;
      this.priceGradient = info.price_gradient;
    },
    getData: function getData() {
      var data = this.form.getFieldsValue();
      data.course_id = +this.$searchQuery.id;
      data.price_gradient = this.priceGradient;
      data.single_reserve = +this.singleReserve;

      if (data.single_reserve) {
        data.single_price = this.singlePrice;
      }

      return data;
    },
    onPriceGradientChange: function onPriceGradientChange(priceGradient) {
      this.priceGradient = priceGradient;
    },
    onSingleReserveChange: function onSingleReserveChange() {
      this.singleReserve = +!this.singleReserve;
    }
  }
};
