import BrandSettingSmsNotice from '@/views/biz-modals/brand/setting/sms/notice';
var componentName = 'notice-item';
export default {
  name: 'NoticeItem',
  bem: {
    bComponent: componentName
  },
  modals: {
    BrandSettingSmsNotice: BrandSettingSmsNotice
  },
  data: function data() {
    return {
      isShowEdit: 0,
      params: {
        id: '',
        msg_suffix: '',
        msg_preffix: '',
        notify_time: '',
        notify_number: '',
        notify_mode: {
          sms: 0,
          app: 0,
          mini_programs: 0
        }
      }
    };
  },
  props: {
    info: {
      type: Object,
      default: function _default() {}
    }
  },
  created: function created() {
    this.init();
  },
  methods: {
    // 初始化回显数据
    init: function init() {
      this.params.notify_time = this.info.notify_time.value;
      this.params.notify_number = this.info.notify_number;
      this.params.msg_preffix = this.info.msg_preffix;
      this.params.msg_suffix = this.info.msg_suffix;
      this.params.notify_mode = {
        sms: this.info.notify_mode.sms && this.info.notify_mode.sms.value,
        mini_programs: this.info.notify_mode.mini_programs && this.info.notify_mode.mini_programs.value
      };
    },
    save: function save() {
      var para = Object.assign({}, this.params, {
        id: this.info.id
      });
      this.$emit('editInfo', para);
      this.isShowEdit = 0;
    }
  }
};
