import { columns, CARD_TYPE } from "./all.config.ts";
import { AllService } from "./all.service";
import tableMixin from '@/mixins/table.mixin';
import CardShopMemberRecoverSale from '@/views/biz-modals/card/shop-member/recover-sale';
import CardShopMemberShelf from '@/views/biz-modals/card/shop-member/shelf';
import CardShopMemberShopTable from '@/views/biz-modals/card/shop-member/shop-table';
import CardShopMemberStopSale from '@/views/biz-modals/card/shop-member/stop-sale';
import CardSpecs from '@/views/biz-components/card/specs';
import { ADMISSION_RANGE, SUPPORT_SALES, SELL_STATUS, PUBLISH_CHANNEL } from '@/constants/card/member';
export default {
  mixins: [tableMixin],
  name: 'PageShopProductMemberAll',
  bem: {
    all: 'page-shop-product-member-list-all'
  },
  components: {
    CardSpecs: CardSpecs
  },
  modals: {
    CardShopMemberRecoverSale: CardShopMemberRecoverSale,
    CardShopMemberShelf: CardShopMemberShelf,
    CardShopMemberShopTable: CardShopMemberShopTable,
    CardShopMemberStopSale: CardShopMemberStopSale
  },
  serviceInject: function serviceInject() {
    return {
      allService: AllService
    };
  },
  rxState: function rxState() {
    return {
      list: this.allService.list$,
      page: this.allService.page$,
      loading: this.allService.loading$,
      publishChannel: this.allService.publishChannel$,
      cardType: this.allService.cardType$,
      sellStatus: this.allService.sellStatus$,
      shelfStatus: this.allService.shelfStatus$,
      cardNumberTypeOptions: this.allService.cardNumberTypeOptions$,
      auth: this.allService.auth$
    };
  },
  computed: {
    columns: columns
  },
  data: function data() {
    return {
      CARD_TYPE: CARD_TYPE,
      ADMISSION_RANGE: ADMISSION_RANGE,
      SUPPORT_SALES: SUPPORT_SALES,
      SELL_STATUS: SELL_STATUS,
      PUBLISH_CHANNEL: PUBLISH_CHANNEL
    };
  },
  methods: {
    /**
     * 查看详情
     */
    goToCardInfo: function goToCardInfo(record) {
      this.$router.push({
        path: "/shop/product/card/member/".concat(CARD_TYPE[record.card_type.id], "/info"),
        query: {
          id: record.id
        }
      });
    },
    // 上架
    onShelf: function onShelf(record) {
      var _this = this;

      this.$modalRouter.push({
        name: 'card-shop-member-shelf',
        props: {
          id: record.id,
          title: "\u4E0A\u67B6".concat(this.$c('member_card'))
        },
        on: {
          success: function success() {
            _this.$router.push({
              query: _this.$searchQuery
            });
          }
        }
      });
    },
    // 编辑
    onEdit: function onEdit(record) {
      var query = {
        id: record.id
      };

      if (record.card_number_type.id === 2) {
        query.type = 'family-card';
      }

      this.$router.push({
        path: "/shop/product/card/member/".concat(this.CARD_TYPE[record.card_type.id], "/edit"),
        query: query
      });
    },
    // 停售
    onStopSale: function onStopSale(record) {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'card-shop-member-stop-sale',
        props: {
          id: +record.id,
          cardType: record.card_type.id,
          cardName: record.card_name
        },
        on: {
          success: function success() {
            _this2.$router.push({
              query: _this2.$searchQuery
            });
          }
        }
      });
    },
    // 恢复售卖
    onRecoverSale: function onRecoverSale(record) {
      var _this3 = this;

      this.$modalRouter.push({
        name: 'card-shop-member-recover-sale',
        props: {
          id: record.id,
          cardType: record.card_type.id,
          cardName: record.card_name
        },
        on: {
          success: function success() {
            _this3.$router.push({
              query: _this3.$searchQuery
            });
          }
        }
      });
    },
    // 删除
    onDelete: function onDelete(record) {
      var _this4 = this;

      this.$confirm({
        title: '确认要删除',
        content: "\u786E\u8BA4\u5220\u9664".concat(record.card_name).concat(this.$c('member_card'), "\uFF1F"),
        onOk: function onOk() {
          return _this4.allService.deleteCard(record.id).toPromise().then(function () {
            _this4.$router.reload();
          });
        }
      });
    }
  }
};
