import moment from 'moment';
import { cloneDeep, remove, every, filter, reduce, forEach } from 'lodash-es';
import { EditFixPackageService } from "./edit-fix-package.service";
import CourseSelectCourse from '@/views/biz-modals/course/select-course';
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
export default {
  name: 'ShopFixPackageEdit',
  serviceInject: function serviceInject() {
    return {
      editPackageService: EditFixPackageService
    };
  },
  rxState: function rxState() {
    return {
      transferUnitList: this.editPackageService.transferUnitList$,
      sellTypeList: this.editPackageService.sellTypeList$,
      unitList: this.editPackageService.unitList$,
      editLoading: this.editPackageService.loading$,
      coachList: this.editPackageService.coachList$,
      packageInfo: this.editPackageService.packageInfo$
    };
  },
  components: {
    StImageUpload: StImageUpload
  },
  modals: {
    CourseSelectCourse: CourseSelectCourse
  },
  bem: {
    add: 'page-shop-add-fix-package',
    basic: 'page-shop-add-basic-package'
  },
  data: function data() {
    return {
      packageData: {
        // 售价
        price: undefined,
        // 是否支持团体课 0为不支持 1为支持
        is_team: 0,
        // 团体课上课范围课程id
        team_range: [],
        // 是否支持私教课 0为不支持 1为支持
        is_personal: 0,
        // 私教课上课范围课程id course_id私教课课程id coach_level私教课支持预约的教练等级
        personal_range: [],
        // 售卖开始时间
        start_time: '',
        // 售卖截止时间
        end_time: '',
        // 有效时间值
        valid_time: undefined,
        // 有效时间单位 1:天 2:月 3:年
        valid_time_unit: 1,
        // 允许冻结天数
        frozen_days: undefined,
        // 售卖方式
        sale_mode: [2],
        // 是否可以转让: 0 不可以 1 可以
        is_allow_transfer: 0,
        // 转让费率
        transfer_rate: undefined,
        // 转让单位 1:百分比 2:元
        transfer_unit: 1,
        // 封面对象
        image: {
          image_id: null,
          image_key: ''
        },
        // 课程包介绍
        intro: '',
        // 备注
        remarks: ''
      },
      // 课程范围是否未输入过
      courseIsFirstInput: true,
      // team course
      teamCourseList: [],
      // 备份课程已做的操作
      teamCourseTotalList: {},
      // 配置课程表格是否显示
      teamCourseListIsShow: false,
      // 配置的课程总计
      teamCourseTotalObject: {
        team_times: 0,
        total: 0
      },
      // 批量操作的值
      teamOperationObject: {
        team_times: undefined,
        team_unit_price: undefined
      },
      // 操作栏checkbox是否半选
      teamIndeterminate: false,
      // 操作栏checkbox是全选
      teamCheckAll: false,
      // personal course
      personalCourseList: [],
      // 备份课程已做的操作
      personalCourseTotalList: {},
      // 缓存最后一次选择的教练，用于阻止用户不选择任何一个
      personalCoachListHistory: [],
      // 操作栏教练等级选择的值
      personalAllOperationCoachList: [],
      // 配置的课程总计
      personalCourseTotalObject: {
        personal_times: 0,
        total: 0
      },
      // 批量操作的值
      personalOperationObject: {
        personal_times: undefined,
        personal_unit_price: undefined
      },
      // 配置课程表格是否显示
      personalCourseListIsShow: false,
      // 操作栏checkbox是否半选
      personalIndeterminate: false,
      // 操作栏checkbox是全选
      personalCheckAll: false,
      // 操作栏选择教练等级的dorpdown是否显示
      coachAllOperationDropdownIsShow: false,
      // 课程范围是否未配置
      courseIsNone: false,
      courseErrorText: '',
      form: this.$form.createForm(this),
      start_time: null,
      end_time: null,
      endOpen: false,
      // 是否未传了封面
      imageIsNone: false,
      imageErrorText: '',
      fileList: [],
      cropperModal: {}
    };
  },
  mounted: function mounted() {
    this.init();
  },
  methods: {
    init: function init() {
      var _this = this;

      if (!this.packageInfo.is_team) {
        this.packageInfo.team_times = null;
        this.packageInfo.team_unit_price = null;
        this.packageInfo.team_range = [];
      }

      if (!this.packageInfo.is_personal) {
        this.packageInfo.personal_times = null;
        this.packageInfo.personal_unit_price = null;
        this.packageInfo.personal_range = [];
      }

      if (!this.packageInfo.is_allow_transfer) {
        this.packageInfo.transfer_rate = undefined;
      }

      this.form.setFieldsValue({
        price: this.packageInfo.price,
        start_time: moment(this.packageInfo.start_time * 1000),
        end_time: moment(this.packageInfo.end_time * 1000),
        valid_time: this.packageInfo.valid_time,
        frozen_days: this.packageInfo.frozen_days,
        transfer_rate: this.packageInfo.transfer_rate
      }); // 课程范围

      this.packageData.is_team = this.packageInfo.is_team;
      forEach(this.packageInfo.team_range, function (o) {
        _this.teamCourseList.push({
          team_times: o.team_times,
          team_unit_price: o.team_unit_price,
          courseChecked: false,
          course_id: o.course_id,
          course_name: o.course_name,
          course_category: o.course_category,
          course_type: o.course_type
        });

        _this.teamCourseTotalList[o.course_id] = _this.teamCourseTotalList[o.course_id] || {};
        _this.teamCourseTotalList[o.course_id].team_times = o.team_times;
        _this.teamCourseTotalList[o.course_id].team_unit_price = o.team_unit_price;
      });
      this.packageData.is_personal = this.packageInfo.is_personal;
      forEach(this.packageInfo.personal_range, function (o) {
        var coachGradeList = [];
        o.coach_level.forEach(function (i) {
          coachGradeList.push(i.id);
        });

        _this.personalCourseList.push({
          personal_times: o.personal_times,
          personal_unit_price: o.personal_unit_price,
          coachGradeList: coachGradeList,
          courseChecked: false,
          course_id: o.course_id,
          course_name: o.course_name,
          course_category: o.course_category,
          course_type: o.course_type
        });

        _this.personalCourseTotalList[o.course_id] = _this.personalCourseTotalList[o.course_id] || {};
        _this.personalCourseTotalList[o.course_id].personal_times = o.personal_times;
        _this.personalCourseTotalList[o.course_id].personal_unit_price = o.personal_unit_price;
        _this.personalCourseTotalList[o.course_id].list = cloneDeep(o.coach_level);

        _this.personalCoachListHistory.push(cloneDeep(o.coach_level));
      }); // 售卖时间

      this.start_time = moment(this.packageInfo.start_time * 1000);
      this.end_time = moment(this.packageInfo.end_time * 1000); // 有效时间单位

      this.packageData.valid_time_unit = this.packageInfo.valid_time_unit; // 转让设置

      this.packageData.is_allow_transfer = this.packageInfo.is_allow_transfer;
      this.packageData.transfer_unit = this.packageInfo.transfer_unit; // 售卖方式

      this.packageData.sale_mode = this.packageInfo.sale_mode; // 封面

      this.fileList = [this.packageInfo.image];
      this.packageData.image.image_id = this.fileList[0].image_id;
      this.packageData.image.image_key = this.fileList[0].image_key;
      this.imageIsNone = false;
      this.imageErrorText = ''; // 介绍

      this.packageData.intro = this.packageInfo.intro; // 备注

      this.packageData.remarks = this.packageInfo.remarks;
    },
    // 保存
    save: function save() {
      var _this2 = this;

      this.course_validator();
      this.image_validator();
      this.form.validateFieldsAndScroll(function (err, values) {
        if (!err && !_this2.courseIsNone && !_this2.imageIsNone) {
          _this2.packageData.price = values.price;
          _this2.packageData.valid_time = values.valid_time;
          _this2.packageData.frozen_days = values.frozen_days;
          _this2.packageData.transfer_rate = _this2.packageData.is_allow_transfer ? +values.transfer_rate : undefined;
          _this2.packageData.start_time = "".concat(_this2.start_time.format('YYYY-MM-DD'));
          _this2.packageData.end_time = "".concat(_this2.end_time.format('YYYY-MM-DD'));
          _this2.packageData.team_range = [];

          _this2.teamCourseList.forEach(function (i) {
            _this2.packageData.team_range.push({
              course_id: i.course_id,
              team_times: i.team_times,
              team_unit_price: i.team_unit_price
            });
          });

          _this2.packageData.personal_range = [];

          _this2.personalCourseList.forEach(function (i) {
            var arr = cloneDeep(i.coachGradeList);

            if (i.coachGradeList && i.coachGradeList.length > 0 && i.coachGradeList[0].id) {
              arr = i.coachGradeList.map(function (coachItem) {
                return coachItem.id;
              });
            }

            _this2.packageData.personal_range.push({
              course_id: i.course_id,
              personal_times: i.personal_times,
              personal_unit_price: i.personal_unit_price,
              coach_level: arr
            });
          });

          _this2.packageData.album_id = _this2.packageInfo.album_id; // 这个给后端快捷找到相册使用，前端不需要处理使用

          _this2.editPackageService.editPackage(_this2.packageData).subscribe(function (res) {
            _this2.$router.push({
              path: '/shop/product/course/manage/package/list'
            });
          });
        }
      });
    },
    // checkboxChange
    teamCheckboxChange: function teamCheckboxChange(e) {
      this.packageData.is_team = +e.target.checked;
      this.courseIsFirstInput = false;
      this.teamCourseListIsShow = +e.target.checked;
    },
    personalCheckboxChange: function personalCheckboxChange(e) {
      this.packageData.is_personal = +e.target.checked;
      this.courseIsFirstInput = false;
      this.personalCourseListIsShow = +e.target.checked;
    },
    // team
    teamCourseListShow: function teamCourseListShow() {
      this.teamCourseListIsShow = !this.teamCourseListIsShow;
    },
    // personal
    personalCourseListShow: function personalCourseListShow() {
      this.personalCourseListIsShow = !this.personalCourseListIsShow;
    },
    // 批量操作团课课程包节数
    times_operation_ok: function times_operation_ok(type) {
      var _this3 = this;

      if (type === 'team') {
        forEach(this.teamCourseList, function (o) {
          if (o.courseChecked) {
            o.team_times = +_this3.teamOperationObject.team_times; // 备份批量操作的数据

            _this3.teamCourseTotalList[o.course_id] = _this3.teamCourseTotalList[o.course_id] ? _this3.teamCourseTotalList[o.course_id] : {};
            _this3.teamCourseTotalList[o.course_id].team_times = +_this3.teamOperationObject.team_times;
          }
        });
      } else if (type === 'personal') {
        forEach(this.personalCourseList, function (o) {
          if (o.courseChecked) {
            o.personal_times = +_this3.personalOperationObject.personal_times; // 备份批量操作的数据

            _this3.personalCourseTotalList[o.course_id] = _this3.personalCourseTotalList[o.course_id] ? _this3.personalCourseTotalList[o.course_id] : {};
            _this3.personalCourseTotalList[o.course_id].personal_times = +_this3.personalOperationObject.personal_times;
          }
        });
      }
    },
    // 批量操作团课课程包价格
    unit_price_operation_ok: function unit_price_operation_ok(type) {
      var _this4 = this;

      if (type === 'team') {
        forEach(this.teamCourseList, function (o) {
          if (o.courseChecked) {
            o.team_unit_price = +_this4.teamOperationObject.team_unit_price; // 备份批量操作的数据

            _this4.teamCourseTotalList[o.course_id] = _this4.teamCourseTotalList[o.course_id] ? _this4.teamCourseTotalList[o.course_id] : {};
            _this4.teamCourseTotalList[o.course_id].team_unit_price = +_this4.teamOperationObject.team_unit_price;
          }
        });
      } else if (type === 'personal') {
        forEach(this.personalCourseList, function (o) {
          if (o.courseChecked) {
            o.personal_unit_price = +_this4.personalOperationObject.personal_unit_price; // 备份批量操作的数据

            _this4.personalCourseTotalList[o.course_id] = _this4.personalCourseTotalList[o.course_id] ? _this4.personalCourseTotalList[o.course_id] : {};
            _this4.personalCourseTotalList[o.course_id].personal_unit_price = +_this4.personalOperationObject.personal_unit_price;
          }
        });
      }
    },
    // teamItems input change
    teamInputChange: function teamInputChange(e, type, id) {
      this.teamCourseTotalList[id] = this.teamCourseTotalList[id] ? this.teamCourseTotalList[id] : {};
      this.teamCourseTotalList[id][type] = e;
    },
    // personalItems input change
    personalInputChange: function personalInputChange(e, type, id) {
      this.personalCourseTotalList[id] = this.personalCourseTotalList[id] ? this.personalCourseTotalList[id] : {};
      this.personalCourseTotalList[id][type] = e;
    },
    // 添加课程
    addCourse: function addCourse(type) {
      var that = this;

      if (type === 'team') {
        this.$modalRouter.push({
          name: 'course-select-course',
          props: {
            type: type,
            courses: this.teamCourseList
          },
          on: {
            ok: function ok(data) {
              data.list.forEach(function (i) {
                i.courseChecked = false;
                i.team_times = null;
                i.team_unit_price = null;
              });
              forEach(that.teamCourseTotalList, function (value, key) {
                forEach(data.list, function (innerValue) {
                  if (+innerValue.course_id === +key) {
                    innerValue.team_times = value.team_times;
                    innerValue.team_unit_price = value.team_unit_price;
                  }
                }); // 删除teamCourseTotalList里不存在的course_id项

                if (!data.list.some(function (o) {
                  return +o.course_id === +key;
                })) {
                  delete that.teamCourseTotalList[key];
                }
              });
              that.teamCourseList = cloneDeep(data.list);
            }
          }
        });
      } else if (type === 'personal') {
        this.$modalRouter.push({
          name: 'course-select-course',
          props: {
            type: type,
            courses: this.personalCourseList
          },
          on: {
            ok: function ok(data) {
              var coachGradeList = [];
              forEach(that.coachList, function (i) {
                coachGradeList.push(i.id);
              });
              data.list.forEach(function (i) {
                i.courseChecked = false;
                i.coachGradeList = coachGradeList;
                i.personal_times = null;
                i.personal_unit_price = null;
              });
              forEach(that.personalCourseTotalList, function (value, key) {
                forEach(data.list, function (innerValue) {
                  if (+innerValue.course_id === +key) {
                    innerValue.coachGradeList = cloneDeep(value.list ? value.list : coachGradeList);
                    innerValue.personal_times = value.personal_times;
                    innerValue.personal_unit_price = value.personal_unit_price;
                  }
                }); // 删除personalCoachTotalList里不存在的course_id项

                if (!data.list.some(function (o) {
                  return +o.course_id === +key;
                })) {
                  delete that.personalCourseTotalList[key];
                }
              }); // 缓存选择的教练等级

              that.personalCoachListHistory = [];
              data.list.forEach(function (i) {
                that.personalCoachListHistory.push(i.coachGradeList);
              });
              that.personalCourseList = cloneDeep(data.list);
            }
          }
        });
      }
    },
