import { ChangeService } from "./change.service";
import { cloneDeep } from 'lodash-es';
import { timer } from 'rxjs';
import { ruleOptions } from "./change.config";
import EditableContractNumber from '@/views/biz-components/contract/editable-contract-number.vue';
import { PERSON_TYPE, COURSE_TYPE } from '@/constants/course/small-course';
import moment from 'moment';
export default {
  name: 'ModalSoldUpgradeMemberCard',
  bem: {
    sale: 'modal-sold-deal-sale'
  },
  components: {
    EditableContractNumber: EditableContractNumber
  },
  serviceProviders: function serviceProviders() {
    return [ChangeService];
  },
  serviceInject: function serviceInject() {
    return {
      changeService: ChangeService
    };
  },
  rxState: function rxState() {
    return {
      info: this.changeService.info$,
      smallCourseList: this.changeService.smallCourseList$,
      couponList: this.changeService.couponList$,
      advanceList: this.changeService.advanceList$,
      priceInfo: this.changeService.priceInfo$,
      saleList: this.changeService.saleList$,
      loading: this.changeService.loading$
    };
  },
  props: ['id'],
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      PERSON_TYPE: PERSON_TYPE,
      COURSE_TYPE: COURSE_TYPE,
      form: form,
      decorators: decorators,
      show: false,
      // 小班课列表
      smallCourseList: [],
      // 选择的卡
      selectItem: {},
      // 商品金额
      smallCoursePrice: '0',
      // 原小班课折扣
      surplusPrice: '',
      // 优惠
      couponDropdownVisible: false,
      couponText: '未选择优惠券',
      couponAmount: '',
      selectCoupon: '',
      // 定金
      advanceDropdownVisible: false,
      advanceText: '未选择定金',
      advanceAmount: '',
      selectAdvance: '',
      // 减免金额
      reduceAmount: '',
      // 描述
      description: ''
    };
  },
  computed: {
    isAmountDisabled: function isAmountDisabled() {
      // 1 固定排课 2自主排课
      return this.selectItem.course_type === this.COURSE_TYPE.FIXED_COURSE;
    },
    validTime: function validTime() {
      if (this.selectItem.course_begin_time) {
        return this.selectItem.course_begin_time + '至' + this.selectItem.course_end_time;
      }

      return '无';
    },
    // 小计判断
    orderAmountText: function orderAmountText() {
      return this.priceInfo < 0 ? '小计不能为负' : '';
    },
    // 计算小计用
    priceOB: function priceOB() {
      return "".concat(this.selectItem.course_id, "-\n              ").concat(this.selectAdvance, "-\n              ").concat(this.reduceAmount, "-\n              ").concat(this.selectCoupon, "-\n              ").concat(this.surplusPrice);
    }
  },
  watch: {
    // 计算小计
    priceOB: function priceOB() {
      this.getPrice({
        product_id: this.selectItem.course_id,
        product_type: this.info.contract_type,
        advance_id: this.selectAdvance === -1 ? '' : this.selectAdvance,
        reduce_amount: +this.reduceAmount,
        coupon_id: this.selectCoupon === -1 ? '' : this.selectCoupon,
        member_id: +this.info.member_id,
        surplus_amount: +this.surplusPrice
      });
    }
  },
  created: function created() {
    this.changeService.serviceInit(this.id).subscribe();
  },
  methods: {
    moment: moment,
    // 搜索小班课
    onSearch: function onSearch(data) {
      if (data === '') {
        this.smallCourseList.commit(function () {
          return [];
        });
      } else {
        this.changeService.getSmallCourseList(data).subscribe();
      }
    },
    // 重置规格/开卡方式/有效时间/商品金额/购买赠送/表单重置
    resetData: function resetData() {
      // 商品金额
      this.coursePrice = '0';
      this.form.resetFields();
    },
    onSelectCourseChange: function onSelectCourseChange(data) {
      if (data) {
        this.selectItem = this.smallCourseList.filter(function (item) {
          return item.course_id === data;
        })[0];
        this.form.setFieldsValue({
          course_num: this.selectItem.buy_num
        });
      } else {
        // 清空
        this.selectItem = {};
        this.resetData();
      }
    },
    // 原卡折扣
    onSurplusPriceChange: function onSurplusPriceChange(data) {
      this.surplusPrice = data;
    },
    // 优惠
    onSelectCouponChange: function onSelectCouponChange(data) {
      if (data.target.value === -1) {
        this.couponAmount = '';
        this.couponText = '未选择优惠券';
        return;
      }

      var price = this.couponList.filter(function (o) {
        return o.id === data.target.value;
      })[0].price;
      this.couponAmount = price;
      this.couponText = "".concat(price, "\u5143");
    },
    onSelectCoupon: function onSelectCoupon() {
      var _this = this;

      timer(200).subscribe(function () {
        _this.couponDropdownVisible = false;
      });
    },
    resetCoupon: function resetCoupon() {
      this.changeService.resetCouponList();
      this.couponText = '未选择优惠券';
      this.couponAmount = '';
      this.selectCoupon = '';
    },
    // 定金
    onSelectAdvanceChange: function onSelectAdvanceChange(data) {
      if (data.target.value === -1) {
        this.advanceAmount = '';
        this.advanceText = '未选择定金';
        return;
      }

      var price = this.advanceList.filter(function (o) {
        return o.id === data.target.value;
      })[0].price;
      this.advanceAmount = price;
      this.advanceText = "".concat(price, "\u5143");
    },
    onSelectAdvance: function onSelectAdvance() {
      var _this2 = this;

      timer(200).subscribe(function () {
        _this2.advanceDropdownVisible = false;
      });
    },
    resetAdvance: function resetAdvance() {
      this.changeService.resetAdvanceList();
      this.advanceText = '未选择定金';
      this.advanceAmount = '';
      this.selectAdvance = '';
    },
    // 计算实付金额
    getPrice: function getPrice(params) {
      this.changeService.priceAction$.dispatch(params);
    },
    onCreateOrder: function onCreateOrder() {
      var _this3 = this;

      this.form.validate().then(function (values) {
        _this3.changeService.changeSmallCourse({
          course_id: values.course_id,
          course_num: values.course_num,
          contract_number: _this3.info.contract_number,
          old_course_price: +values.old_course_price,
          coupon_id: _this3.selectCoupon || undefined,
          advance_id: _this3.selectAdvance || undefined,
          reduce_price: +_this3.reduceAmount,
          order_price: +_this3.priceInfo,
          sales_id: +values.saleName,
          remarks: _this3.description
        }, _this3.id).subscribe(function (res) {
          _this3.show = false;

          _this3.$emit('success', {
            type: 'create',
            orderId: res.info.order_id
          });
        });
      });
    },
    onPay: function onPay() {
      var _this4 = this;

      this.form.validate().then(function (values) {
        _this4.changeService.changeSmallCoursePay({
          course_id: values.course_id,
          course_num: values.course_num,
          contract_number: _this4.info.contract_number,
          old_course_price: +values.old_course_price,
          coupon_id: _this4.selectCoupon || undefined,
          advance_id: _this4.selectAdvance || undefined,
          reduce_price: +_this4.reduceAmount,
          order_price: +_this4.priceInfo,
          sales_id: +values.saleName,
          remarks: _this4.description
        }, _this4.id).subscribe(function (res) {
          _this4.show = false;

          _this4.$emit('success', {
            type: 'createPay',
            orderId: res.info.order_id
          });
        });
      });
    }
  }
};
