import { EditService } from "../edit.service";
import { MessageService } from '@/services/message.service';
import { UserService } from '@/services/user.service';
import { cloneDeep } from 'lodash-es';
import { ruleOptions } from "../form.config";
import { GradientService } from '@/views/fragments/course/personal#/gradient.service';
import { PatternService } from '@/services/pattern.service';
import { CLASS_STATUS } from '@/constants/course/small-course';
export default {
  name: 'SetSellPrice',
  serviceInject: function serviceInject() {
    return {
      editService: EditService,
      messageService: MessageService,
      userService: UserService,
      gradientService: GradientService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.editService.loading$,
      unitList: this.editService.unitList$,
      sellType: this.editService.sellType$
    };
  },
  components: {},
  props: {
    courseName: {
      type: String,
      default: ''
    },
    courseId: {
      type: Number,
      default: 0
    },
    info: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  data: function data(vm) {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      isShowTransfer: false,
      CLASS_STATUS: CLASS_STATUS
    };
  },
  computed: {
    isDisabled: function isDisabled() {
      return this.CLASS_STATUS.SIGNING_UNCLASSED === this.info.info.class_status || this.CLASS_STATUS.SIGNING_CLASSED === this.info.info.class_status || this.CLASS_STATUS.CLASSED === this.info.info.class_status;
    }
  },
  watch: {
    courseName: function courseName(val) {
      this.form.setFieldsValue({
        course_name: val
      });
    },
    courseId: function courseId(val) {
      this.form.setFieldsValue({
        course_id: val
      });
    }
  },
  mounted: function mounted() {
    var curTime = moment();
    var start = cloneDeep(curTime);
    var end = cloneDeep(curTime);
    this.form.setFieldsValue({
      apply_date: [start.add('30', 'minutes'), end.add('31', 'minutes')]
    });
    this.setFieldsValue();
  },
  methods: {
    setFieldsValue: function setFieldsValue() {
      var info = this.info.price;
      this.form.setFieldsValue({
        course_name: info.course_name,
        is_allow_transfer: info.is_allow_transfer,
        transfer_num: info.transfer_num,
        sell_type: info.sell_type,
        transfer_type: info.transfer_type,
        sales_price: info.sales_price
      });

      if (info.apply_begin_time) {
        this.form.setFieldsValue({
          apply_date: [moment(info.apply_begin_time), moment(info.apply_end_time)]
        });
      }

      this.isShowTransfer = info.is_allow_transfer;
    },
    save: function save(para) {
      var _this = this;

      this.form.validateFields().then(function (values) {
        values.course_id = _this.courseId;
        values.apply_begin_time = values.apply_date[0].format('YYYY-MM-DD HH:mm');
        values.apply_end_time = values.apply_date[1].format('YYYY-MM-DD HH:mm');
        values.is_allow_transfer = values.is_allow_transfer ? 1 : 0;
        values.is_release = para;

        if (values.is_allow_transfer === 0) {
          values.transfer_type = undefined;
          values.transfer_num = undefined;
        }

        delete values.apply_date;

        _this.editService.setPrice(values).subscribe(_this.onSaveSuccess);
      });
    },
    onSaveSuccess: function onSaveSuccess() {
      this.messageService.success({
        content: '提交成功'
      });
      this.$router.push({
        name: 'shop-product-course-manage-small-course-list'
      });
    },
    transferChange: function transferChange(val) {
      this.isShowTransfer = val;
    },
    disabledDate: function disabledDate(current) {
      return current && current.format('YYYY-MM-DD') < moment().format('YYYY-MM-DD');
    }
  }
};
