import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import CustomLink from '@/views/biz-components/custom-link/custom-link.vue';
import { cloneDeep } from 'lodash-es';
import { H5SettingIndexService } from "../h5-setting-index.service";
import { MessageService } from '@/services/message.service';
export default {
  name: 'h5FormFnComponent',
  bem: {
    module: 'h5-form-fn-component'
  },
  props: {
    value: {
      type: Object
    },
    moduleName: {
      type: String,
      default: '功能入口'
    },
    maxNum: {
      type: Number,
      default: 15
    }
  },
  data: function data() {
    return {
      pathList: [],
      canDrag: true
    };
  },
  components: {
    StImageUpload: StImageUpload,
    CustomLink: CustomLink
  },
  serviceInject: function serviceInject() {
    return {
      h5SettingIndexService: H5SettingIndexService,
      messageService: MessageService
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.h5SettingIndexService.getPathList().subscribe(function (res) {
      _this.pathList = res.list;
    });
  },
  methods: {
    addFnMenu: function addFnMenu() {
      this.value.list.unshift({
        image: {},
        title: '',
        link: {}
      });
      this.changeFn();
    },
    updateImg: function updateImg(e, index) {
      if (e.length) {
        this.value.list[index].image = e[0];
        this.changeFn();
      }
    },
    deleteFn: function deleteFn(index) {
      if (this.value.list.length < 3) {
        return this.messageService.warn({
          content: '至少添加2项功能入口'
        });
      }

      this.value.list.splice(index, 1);
      this.changeFn();
    },
    handleLinkChange: function handleLinkChange(link, index) {
      this.value.list[index].link = link;
      this.changeFn();
    },
    changeFn: function changeFn() {
      this.$emit('change');
    }
  }
};
