import { RuleConfig } from '@/constants/staff/rule';
import { EditService } from "../edit.service";
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import ShopSelect from '@/views/biz-components/shop-select';
import DepartmentSelect from '@/views/biz-components/department-select';
import { PatternService } from '@/services/pattern.service';
import { ruleOptions } from "../staff-form.config.ts";
import FaceUpload from '@/views/biz-components/face-upload/face-upload';
import { cloneDeep } from 'lodash-es';
import { UserService } from '@/services/user.service';
export default {
  name: 'EditBasicInfo',
  bem: {
    editBasicInfo: 'page-brand-staff-edit-basic'
  },
  serviceInject: function serviceInject() {
    return {
      rules: RuleConfig,
      pattern: PatternService,
      editService: EditService,
      userService: UserService
    };
  },
  rxState: function rxState() {
    return {
      roleList: this.editService.roleList$,
      codeList: this.editService.codeList$,
      loading: this.editService.loading$,
      id_types: this.userService.getOptions$('staff.id_type'),
      sexs: this.userService.getOptions$('staff.sex'),
      nature_works: this.userService.getOptions$('staff.nature_work')
    };
  },
  props: {
    data: {
      type: Object
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      fileList: [],
      faceList: [],
      countryList: [],
      id_type: '',
      country_code_id: '',
      value: '' // 部门选择

    };
  },
  components: {
    ShopSelect: ShopSelect,
    DepartmentSelect: DepartmentSelect,
    FaceUpload: FaceUpload,
    StImageUpload: StImageUpload
  },
  computed: {
    isSuperAdmin: function isSuperAdmin() {
      return !!this.data.is_super_admin;
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.$nextTick().then(function () {
      var form = cloneDeep(_this.data);

      _this.setData(form);
    });
  },
  methods: {
    resetID: function resetID() {
      this.form.setFieldsValue({
        id_number: undefined
      });
    },
    onChangeGetAvatar: function onChangeGetAvatar(imageFiles) {
      this.fileList = cloneDeep(imageFiles);
    },
    onChangeGetFace: function onChangeGetFace(imageFiles) {
      this.faceList = cloneDeep(imageFiles);
    },
    roleChange: function roleChange(v) {
      if (v && v.length > 10) v.pop();
      console.log(v);
    },
    onChange: function onChange(value) {
      console.log('选择部门', value);
      this.value = value;
    },
    onSelectIdtype: function onSelectIdtype(e) {
      console.log('证件选择', e);
    },
    saveAndGoNext: function saveAndGoNext(e) {
      var _this2 = this;

      this.form.validate().then(function (values) {
        _this2.submit(values, 1);
      });
    },

    /**
     * saveOrgoNext 0 保存 1 下一个
     */
    submit: function submit(data, saveOrgoNext) {
      var _this3 = this;

      if (data.entry_date) {
        data.entry_date = moment(data.entry_date).format('YYYY-MM-DD');
      }

      data.album_id = this.data.album_id;
      data.image_avatar = this.fileList[0] || {};
      data.image_face = this.faceList[0] || {};
      data.country_code_id = this.country_code_id;
      data.id_type = this.id_type;
      data.department_id = +data.department_id;
      this.editService.updateBasicInfo(this.data.staff_id, data).subscribe(function (res) {
        _this3.$emit('goNext');

        _this3.$emit('updateStaffInfo');
      });
    },
    setData: function setData(obj) {
      this.form.setFieldsValue({
        staff_name: obj.staff_name,
        // 姓名
        nickname: obj.nickname,
        // 昵称
        mobile: obj.mobile,
        // 手机号
        staff_num: obj.staff_num,
        // 工号
        sex: obj.sex,
        // 性别
        id_number: obj.id_number,
        // 身份证
        nature_work: obj.nature_work || undefined,
        // 工作性质
        department_id: String(obj.department_id) || undefined,
        // 部门
        role_id: obj.role_id,
        // 角色
        shop_id: obj.shop_id,
        // 所属门店
        entry_date: obj.entry_date ? moment(obj.entry_date) : undefined,
        // 入职时间
        mail: obj.mail // 邮箱

      });
      this.country_code_id = obj.country_code_id;
      this.id_type = obj.id_type;
      this.fileList = Array.isArray(obj.image_avatar) ? obj.image_avatar : [obj.image_avatar];
      this.faceList = Array.isArray(obj.image_face) ? obj.image_face : [obj.image_face];
    }
  }
};
