import GroupForm from "./components#/group-form.vue";
import { EditDepositService } from "./edit-deposit.service";
import { columnsGroupStored, ruleOptions } from "./add-deposit.config";
import SelectShop from '@/views/fragments/shop/select-shop';
import { PatternService } from '@/services/pattern.service';
import moment, { months } from 'moment';
import { ACTIVITY_STATUS } from '@/constants/marketing/group-buy';
export default {
  name: 'PageBrandMarketingGroupEditDeposit',
  bem: {
    basic: 'brand-marketing-group-deposit'
  },
  serviceInject: function serviceInject() {
    return {
      edit: EditDepositService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.edit.loading$,
      info: this.edit.info$,
      list: this.edit.list$
    };
  },
  components: {
    GroupForm: GroupForm
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      columnsGroupStored: columnsGroupStored,
      groupParams: {
        type: 2,
        id: null
      },
      activityState: Number,
      // 当前活动活动状态
      depositId: '',
      // 回显下拉选中product
      currentStored: [],
      // 当前下拉选中详细
      shopIds: [],
      ACTIVITY_STATUS: ACTIVITY_STATUS,
      shopList: null,
      tableText: '',
      tableErr: ''
    };
  },
  mounted: function mounted() {
    this.setFieldsValue();
  },
  methods: {
    setPriceChange: function setPriceChange() {
      if (!this.currentStored[0].group_price) {
        this.tableText = '请输入拼团价格';
        this.tableErr = true;
      } else {
        this.tableText = '';
        this.tableErr = false;
      }
    },
    changeSelect: function changeSelect(id) {
      var _this = this;

      this.form.setFieldsValue({
        depositId: id
      });
      this.list.list.filter(function (item) {
        if (item.id == id) {
          _this.currentStored = item.product_spec;
        }
      });
      this.groupParams.id = this.depositId;
    },
    onSubmit: function onSubmit(params) {
      var _this2 = this;

      var tmpList = [{
        sku_id: this.currentStored[0].sku_id,
        group_price: this.currentStored[0].group_price
      }];
      params.id = this.$searchQuery.id;
      params.product_type = 2;
      params.product_id = this.depositId;
      params.sku = tmpList;
      this.edit.editGroup(params).subscribe(function (res) {
        _this2.$router.push({
          path: "/brand/marketing/plugin/group/list"
        });
      });
    },
    setFieldsValue: function setFieldsValue() {
      this.activityState = this.info.activity_state.id;
      this.depositId = this.info.product.id;
      this.currentStored = this.info.sku;
      this.shopList = this.info.support_shop || [];
      this.form.setFieldsValue({
        depositId: this.info.product.id
      });
    }
  }
};
