import { ListService } from "./list.service";
import { UserService } from '@/services/user.service';
import MarkteingPluginTitle from "../../components#/marketing-title";
import tableMixin from '@/mixins/table.mixin';
import { columns } from "./list.config";
import { TYPE } from '@/constants/marketing/plugin';
import useShare from '@/hooks/marketing/share.hook';
import BrandMarketingCouponTable from '@/views/biz-modals/brand/marketing/coupon-table';
export default {
  name: 'PageBrandMarketingPluginCouponList',
  mixins: [tableMixin],
  bem: {
    basic: 'page-brand-plugin-coupon'
  },
  modals: {
    BrandMarketingCouponTable: BrandMarketingCouponTable
  },
  serviceInject: function serviceInject() {
    return {
      listService: ListService,
      userService: UserService
    };
  },
  rxState: function rxState() {
    return {
      list: this.listService.list$,
      page: this.listService.page$,
      loading: this.listService.loading$,
      info: this.listService.info$,
      brand: this.listService.brand$,
      auth: this.listService.auth$,
      productType: this.listService.productType$
    };
  },
  hooks: function hooks() {
    return {
      share: useShare()
    };
  },
  data: function data(vm) {
    return {
      TYPE: TYPE,
      couponName: '',
      couponStatus: -1,
      columns: columns(vm)
    };
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  watch: {
    query: function query(newVal) {
      this.setSearchData();
    }
  },
  methods: {
    onConfigHandle: function onConfigHandle() {
      this.$router.push({
        path: '/brand/setting/mina/index'
      });
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      var _this$$searchQuery = this.$searchQuery,
          coupon_name = _this$$searchQuery.coupon_name,
          coupon_status = _this$$searchQuery.coupon_status;
      this.couponName = coupon_name;
      this.couponStatus = coupon_status || -1;
    },
    // 编辑
    onEdit: function onEdit(record) {
      this.$router.push({
        path: '/brand/marketing/plugin/coupon/edit',
        query: {
          id: record.id
        }
      });
    },
    onGeneralize: function onGeneralize(record) {
      var _this = this;

      var isAuth = record.is_auth;
      this.listService.getPosterInfo(record.id).subscribe(function (res) {
        var shsInfo = {
          qrcode_url: _this.info.qrcode_url,
          brand_logo: _this.info.brand_logo,
          brand_name: _this.info.brand_name,
          price: _this.info.price
        };

        _this.share.poster({
          isAuth: isAuth,
          shsInfo: shsInfo,
          shsPath: '/saas/poster'
        });
      });
    },
    // 停止优惠券模板
    onStop: function onStop(record) {
      var that = this;
      this.$confirm({
        title: '提示',
        content: '结束后当用户进入投放该优惠券的活动时，将无法领取该优惠券。确认要结束？',
        onOk: function onOk() {
          that.listService.stopMarketingCoupon(record.id).subscribe(function (res) {
            that.$router.reload();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    // 新增优惠券活动
    onAddCoupon: function onAddCoupon() {
      this.$router.push({
        path: '/brand/marketing/plugin/coupon/add'
      });
    },
    goReceive: function goReceive(record) {
      this.$router.push({
        path: '/brand/marketing/plugin/coupon/receive',
        query: {
          id: record.id
        }
      });
    }
  },
  // FIXME: 改用枚举
  filters: {
    couponTypeFilter: function couponTypeFilter(val) {
      switch (val) {
        case 1:
          return '普通券';

        case 2:
          return '活动券';

        default:
          return '';
      }
    },
    putStatusFilter: function putStatusFilter(val) {
      switch (val) {
        case 1:
          return '未投放';

        case 2:
          return '已投放';

        default:
          return '';
      }
    }
  },
  components: {
    MarkteingPluginTitle: MarkteingPluginTitle
  }
};
