import { AddService } from "../../add.service";
import { MessageService } from '@/services/message.service';
import SelectShop from '@/views/fragments/shop/select-shop';
import SelectCoach from '@/views/fragments/coach/select-coach';
import { ruleOptions } from "../set-shop-coach.config";
import { SHOP_SETTING } from '@/constants/course/personal';
export default {
  name: 'SetShopCoach',
  serviceInject: function serviceInject() {
    return {
      addService: AddService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.addService.loading$,
      shopSetting: this.addService.shopSetting$
    };
  },
  components: {
    SelectShop: SelectShop,
    SelectCoach: SelectCoach
  },
  props: {
    courseName: {
      type: String,
      default: ''
    },
    courseId: {
      type: Number,
      default: 0
    }
  },
  watch: {
    courseName: function courseName(val) {
      this.form.setFieldsValue({
        course_name: val
      });
    },
    courseId: function courseId(val) {
      this.form.setFieldsValue({
        course_id: val
      });
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      isShow: false,
      shopIds: []
    };
  },
  methods: {
    save: function save(e) {
      var _this = this;

      e.preventDefault();
      this.form.validate().then(function (values) {
        values.course_id = _this.courseId;

        _this.addService.setShop(values).subscribe(function () {
          _this.messageService.success({
            content: '提交成功'
          });

          _this.$emit('goNext');
        });
      });
    },
    onChange: function onChange(e) {
      this.isShow = e.target.value === SHOP_SETTING.SPECIFIED_STORE;
      this.shopIds = [];
    },
    onSelectShopChange: function onSelectShopChange(shopIds) {
      this.shopIds = shopIds;
      this.form.setFieldsValue({
        shop_ids: shopIds
      });
    },
    onSelectCoachChange: function onSelectCoachChange(coachIds) {
      this.form.setFieldsValue({
        coach_ids: coachIds
      });
    },
    setFieldsValue: function setFieldsValue() {
      var info = this.info;
      this.form.setFieldsValue({
        course_name: info.course_name,
        course_category: info.course_category,
        train_aim: info.train_aim,
        duration: info.duration,
        is_online_sale: info.is_online_sale,
        price: info.price,
        effective_unit: info.effective_unit,
        image: info.image,
        description: info.description
      });
      this.fileList = [this.info.image];
    }
  }
};
