import { InfoService } from "./info.service";
import { cloneDeep } from 'lodash-es';
import ShopMapLocation from '@/views/biz-modals/shop/map-location';
import CheckboxFacilityInfo from '@/views/biz-components/checkbox-facility/checkbox-facility-info';
import ShopHourPicker from '@/views/biz-components/shop-hour-picker/shop-hour-picker';
import BrandSettingShopHoliday from '@/views/biz-modals/brand/setting/shop/holiday';
export default {
  name: 'PageShopSettingShopInfo',
  bem: {
    shopInfo: 'page-shop-setting-shop-info'
  },
  components: {
    CheckboxFacilityInfo: CheckboxFacilityInfo,
    ShopHourPicker: ShopHourPicker
  },
  modals: {
    ShopMapLocation: ShopMapLocation,
    BrandSettingShopHoliday: BrandSettingShopHoliday
  },
  serviceInject: function serviceInject() {
    return {
      infoService: InfoService
    };
  },
  rxState: function rxState() {
    return {
      serviceList: this.infoService.serviceList$,
      info: this.infoService.info$,
      auth: this.infoService.auth$
    };
  },
  computed: {
    serviceListComputed: function serviceListComputed() {
      var _this = this;

      var array = cloneDeep(this.serviceList);
      array.forEach(function (i) {
        i.selected = _this.info.shop_services.some(function (o) {
          return o.service_id === i.service_id;
        });
      });
      return array;
    }
  },
  data: function data() {
    return {
      flagList: ['', 'shop-trial', 'shop-opening', 'shop-stop', 'shop-close', 'shop-holiday']
    };
  },
  methods: {
    // 查看定位
    location: function location() {
      this.$modalRouter.push({
        name: 'shop-map-location',
        props: {
          // lat: '31.230350',
          // lng: '121.473720'
          lat: this.info.lat,
          lng: this.info.lng
        }
      });
    },
    // 编辑
    onEdit: function onEdit() {
      this.$router.push({
        path: '/shop/setting/shop/edit'
      });
    },
    // 设置放假
    onSetChange: function onSetChange() {
      this.$router.reload();
    }
  }
};
