import { IndexService } from "./index.service";
import MarkteingPluginTitle from "../../components#/marketing-title";
import { columns } from "./index.config.ts";
import tableMixin from '@/mixins/table.mixin';
import { ACTIVITY_STATUS } from '@/constants/marketing/lottery';
import { TYPE } from '@/constants/marketing/plugin';
import useShare from '@/hooks/marketing/share.hook';
import BrandMarketingPluginLotteryTable from '@/views/biz-modals/brand/marketing/lottery-table';
export default {
  name: 'PluginLotteryIndex',
  mixins: [tableMixin],
  modals: {
    BrandMarketingPluginLotteryTable: BrandMarketingPluginLotteryTable
  },
  data: function data() {
    return {
      ACTIVITY_STATUS: ACTIVITY_STATUS,
      TYPE: TYPE
    };
  },
  bem: {
    bPage: 'page-brand-marketing-plugin-lottery-index'
  },
  serviceInject: function serviceInject() {
    return {
      indexService: IndexService
    };
  },
  hooks: function hooks() {
    return {
      share: useShare()
    };
  },
  rxState: function rxState() {
    return {
      list: this.indexService.list$,
      page: this.indexService.page$,
      loading: this.indexService.loading$,
      info: this.indexService.info$,
      status: this.indexService.status$,
      auth: this.indexService.auth$
    };
  },
  components: {
    MarkteingPluginTitle: MarkteingPluginTitle
  },
  computed: {
    columns: columns
  },
  methods: {
    onConfigHandle: function onConfigHandle() {
      this.$router.push({
        path: '/brand/setting/mina/index'
      });
    },
    onGeneralize: function onGeneralize(record) {
      var _this = this;

      // let is_auth = record.is_auth
      // 绑定小程序
      this.indexService.getPosterInfo(record.id).subscribe(function (res) {
        var info = _this.info;
        var shsInfo = {
          qrcode_url: info.qrcode_url,
          sub_name: info.sub_name
        };
        var isAuth = res.is_auth;

        _this.share.poster({
          isAuth: isAuth,
          shsInfo: shsInfo,
          shsPath: '/saas/lottery_poster'
        });
      });
    },
    // 停止优惠券模板
    onStop: function onStop(record) {
      if (record.activity_status === this.ACTIVITY_STATUS.ISSTOPED) {
        // this.$router.push({
        //   path: '/brand/marketing/plugin/lottery/info/prize',
        //   query: {
        //     id: record.id
        //   }
        // })
        this.$modalRouter.push({
          name: 'brand-marketing-plugin-lottery-table',
          props: {
            id: record.id,
            title: '奖品数据'
          }
        });
        return;
      }

      var that = this;
      this.$confirm({
        title: '提示',
        okText: record.activity_status === this.ACTIVITY_STATUS.ISHOLDING ? '取消' : '结束',
        cancelText: '我再想想',
        content: record.activity_status === this.ACTIVITY_STATUS.ISHOLDING ? '确认取消该活动' : '确认要结束',
        onOk: function onOk() {
          that.indexService.stopActivity(record.id).subscribe(function (res) {
            that.$router.reload();
          });
        }
      });
    }
  }
};
