import { RoleService } from "./role.service";
export default {
  name: 'RoleConfig',
  bem: {
    role: 'page-role',
    roleList: 'page-role-list',
    roleView: 'page-role-view',
    roleContent: 'page-role-content'
  },
  serviceInject: function serviceInject() {
    return {
      roleService: RoleService
    };
  },
  rxState: function rxState() {
    return {
      roles: this.roleService.roleList$,
      stat: this.roleService.stat$,
      auth: this.roleService.auth$
    };
  },
  data: function data() {
    return {
      isActive: false
    };
  },
  methods: {
    onClickEditRole: function onClickEditRole(id) {
      this.$router.push({
        name: 'brand-staff-role-edit',
        query: {
          roleId: id
        }
      });
    },
    onCLickAddRole: function onCLickAddRole() {
      this.$router.push({
        name: 'brand-staff-role-add'
      });
    },
    onClickDelRole: function onClickDelRole(role_id) {
      this.roleService.del({
        role_id: role_id
      }).subscribe();
    },
    getTreeNodeOnclick: function getTreeNodeOnclick(role, e) {
      this.$nextTick().then(function () {
        var doms = document.querySelectorAll('.page-role-list__tree .item');
        var roleOpreation = document.querySelectorAll('.page-role-list__tree .item .role-opreation');
        roleOpreation.forEach(function (dom) {
          dom.setAttribute('class', 'role-opreation');
        });
        doms.forEach(function (dom) {
          dom.setAttribute('class', 'item pd-y8  pd-x16');
        });
        e.currentTarget.querySelector('.role-opreation').setAttribute('class', 'role-opreation active');
        e.currentTarget.setAttribute('class', 'item active pd-y8  pd-x16');
      });
      this.$router.push({
        name: 'brand-staff-role-info',
        query: {
          id: role.id
        }
      });
    }
  }
};
