import moment from 'moment';
import { TransferService } from "./transfer.service";
import { cloneDeep, get } from 'lodash-es';
import { PatternService } from '@/services/pattern.service';
import { OPERATION_TYPES } from '@/constants/sold/operations';
import { ruleOptions } from "./transfer.config";
import EditableContractNumber from '@/views/biz-components/contract/editable-contract-number.vue';
import MemberSearch from '@/views/biz-components/member-search/member-search';
import { PERSON_TYPE } from '@/constants/course/small-course';
export default {
  name: 'ModalSoldCourseTransfer',
  bem: {
    transfer: 'modal-sold-course-transfer'
  },
  components: {
    EditableContractNumber: EditableContractNumber,
    MemberSearch: MemberSearch
  },
  serviceProviders: function serviceProviders() {
    return [TransferService];
  },
  serviceInject: function serviceInject() {
    return {
      transferService: TransferService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.transferService.loading$,
      packageCourseInfo: this.transferService.packageCourseInfo$,
      packageTransferInfo: this.transferService.packageTransferInfo$,
      timeScope: this.transferService.timeScope$,
      personalCourseInfo: this.transferService.personalCourseInfo$,
      memberPaymentlist: this.transferService.memberPaymentlist$
    };
  },
  computed: {
    isPackage: function isPackage() {
      return this.type === 'package';
    },
    isPersonal: function isPersonal() {
      return this.type === 'personal';
    },
    isCanAddNewMember: function isCanAddNewMember() {
      if (this.isPackage) {
        return this.packageTransferInfo.can_add_member === 1 ? 1 : 2;
      }

      if (this.isPersonal) {
        return this.personalCourseInfo.can_add_member === 1 ? 1 : 2;
      }

      return 2;
    }
  },
  props: ['id', 'type'],
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      PERSON_TYPE: PERSON_TYPE,
      form: form,
      decorators: decorators,
      OPERATION_TYPES: OPERATION_TYPES,
      show: false,
      // 转让信息
      transferData: {
        member_id: null,
        member_name: '',
        mobile: '',
        start_time: '',
        remain_price: null,
        contract_number: '',
        handling_fee: null,
        frozen_pay_type: null
      },
      endTime: moment(),
      poundage: '',
      isSelectMember: false
    };
  },
  created: function created() {
    var _this = this;

    this.transferService.getCourseInfo(this.id, this.type).subscribe(function (res) {
      if (_this.isPackage) {
        _this.endTime = moment(_this.packageTransferInfo.course_end_time * 1000);
        _this.poundage = _this.packageTransferInfo.poundage;

        _this.transferService.getMemberPaymentList({
          member_id: get(res, 'info.transfer_info.member_id'),
          product_type: 4,
          operation_type: OPERATION_TYPES.TRANSFORM
        }).subscribe();
      }

      if (_this.isPersonal) {
        _this.endTime = moment(res.info.end_time * 1000);
        _this.poundage = res.info.poundage;

        _this.transferService.getMemberPaymentList({
          member_id: get(res, 'info.member_id'),
          product_type: 2,
          operation_type: OPERATION_TYPES.TRANSFORM
        }).subscribe();
      }
    });
  },
  methods: {
    onSubmit: function onSubmit() {
      var _this2 = this;

      this.form.validate().then(function (values) {
        var sold_type = _this2.isPackage ? _this2.packageTransferInfo.sold_type : _this2.isPersonal ? _this2.personalCourseInfo.sold_type : '1';
        /**
         * info 混乱，需要重构，暂不处理
         */

        var info = _this2.isPersonal ? _this2.personalCourseInfo : _this2.packageTransferInfo;

        _this2.transferService.editCourseTransfer({
          member_id: +values.member_id || 0,
          member_name: values.member_name,
          mobile: values.mobile ? values.mobile.phone : undefined,
          remain_price: +values.remainPrice,
          contract_number: info.contract_number,
          frozen_pay_type: +values.payType,
          sold_type: +sold_type,
          handling_fee_reduce: values.handling_fee_reduce,
          is_minors: values.is_minors,
          parent_name: values.parent_name,
          parent_mobile: values.parent_mobile ? values.parent_mobile.phone : undefined,
          parent_country_prefix: values.parent_mobile ? values.parent_mobile.code_id : undefined,
          parent_user_role: values.parent_user_role
        }, _this2.id, _this2.type).subscribe(function (res) {
          _this2.$emit('success');

          _this2.show = false;
        });
      });
    },
    // time
    moment: moment,
    disabledStartDate: function disabledStartDate(startValue) {
      return startValue.valueOf() < moment().subtract(1, 'd').valueOf() || startValue.valueOf() > moment().add(31, 'd').valueOf();
    },
    onStartTimeChange: function onStartTimeChange(data) {
      this.endTime = cloneDeep(moment(data.valueOf() + this.timeScope));
    }
  }
};
