import { defaults } from 'lodash-es';
export default {
  name: 'StModal',
  model: {
    prop: 'visible',
    event: 'change'
  },
  props: {
    size: {
      type: String,
      default: 'default'
    },
    width: {
      type: String,
      default: ''
    },
    wrapClassName: {
      type: String
    },
    loading: {
      type: Boolean,
      default: false
    },
    backgroundImgae: {
      type: String,
      default: ''
    },
    maskClosable: {
      type: Boolean,
      default: true
    }
  },
  data: function data() {
    return {
      /**
       * 挂在 window 对象上
       */
      currentModalName: window.currentModalName || ''
    };
  },
  computed: {
    bodyStyle: function bodyStyle() {
      return this.backgroundImgae.length > 0 ? {
        backgroundImage: "url(".concat(this.backgroundImgae, ")"),
        backgroundSize: '100% 100%'
      } : {};
    },
    isFooterNull: function isFooterNull() {
      return this.$attrs.footer === false || this.$attrs.footer === null;
    },
    computedWidth: function computedWidth() {
      var width = 0;

      if (this.width) {
        width = this.width;
      } else if (this.size === 'default') {
        width = 676;
      } else if (this.size === 'small') {
        width = 484;
      }

      return width;
    }
  },
  methods: {
    close: function close() {
      this.$emit('change', false);
      this.$emit('cancel');
    },
    clickModal: function clickModal() {
      if (this.maskClosable) {
        this.close();
      }
    },
    clickModalContent: function clickModalContent(e) {
      e.stopPropagation();
    }
  },
  mounted: function mounted() {
    /**
     * 需要modal是v-modal-link唤起的 <modal-link> 会有异步问题不要用
     */
    this.antModalEl = document.querySelector('.ant-modal');
    this.antModalContentEl = document.querySelector('.ant-modal-content');

    if (this.antModalEl && this.antModalContentEl) {
      // 模态窗上下点击不关闭
      // console.log(this.antModalEl, this.antModalContentEl)
      this.antModalEl.addEventListener('click', this.clickModal, false);
      this.antModalContentEl.addEventListener('click', this.clickModalContent, false);
      document.body.style = 'overflow:hidden;padding-right:8px;';
    }
  },
  beforeDestroy: function beforeDestroy() {
    if (this.antModalEl && this.antModalContentEl) {
      this.antModalEl.removeEventListener('click', this.clickModal, false);
      this.antModalContentEl.removeEventListener('click', this.clickModalContent, false);
    }
  },
  destroyed: function destroyed() {
    document.body.style = '';
  }
};
