import Calendar from '@/views/biz-components/schedule/calendar';
import { TeamScheduleScheduleService } from '@/views/pages/shop/product/course/schedule/team/service#/schedule.service';
import ScheduleTeamAddCourseBatch from '@/views/biz-modals/schedule/team/add-course-batch';
import ScheduleBatchCourseManage from '@/views/biz-modals/schedule/batch/course-manage';
import ScheduleBatchAddEditCourse from '@/views/biz-modals/schedule/batch/add-edit-course';
import ScheduleBatchCourseRankPreview from '@/views/biz-modals/schedule/batch/course-rank-preview';
import ScheduleTeamAddCourse from '@/views/biz-modals/schedule/team/add-course';
import ScheduleTeamCopySchedule from '@/views/biz-modals/schedule/team/copy-schedule';
import ScheduleTeamReserveInfo from '@/views/biz-modals/schedule/team/reserve-info';
import ScheduleTeamPunishList from '@/views/biz-modals/schedule/team/punish-list';
import ScheduleTeamTimeTable from '@/views/biz-modals/schedule/team/time-table';
import { TeamService } from "./team.service";
import { TIME_UNIT } from '@/constants/course/team';
export default {
  name: 'TeamSchedule',
  modals: {
    ScheduleTeamAddCourseBatch: ScheduleTeamAddCourseBatch,
    ScheduleTeamAddCourse: ScheduleTeamAddCourse,
    ScheduleTeamCopySchedule: ScheduleTeamCopySchedule,
    ScheduleTeamReserveInfo: ScheduleTeamReserveInfo,
    ScheduleBatchCourseManage: ScheduleBatchCourseManage,
    ScheduleBatchAddEditCourse: ScheduleBatchAddEditCourse,
    ScheduleBatchCourseRankPreview: ScheduleBatchCourseRankPreview,
    ScheduleTeamPunishList: ScheduleTeamPunishList,
    ScheduleTeamTimeTable: ScheduleTeamTimeTable
  },
  serviceInject: function serviceInject() {
    return {
      teamSchduleService: TeamScheduleScheduleService,
      service: TeamService
    };
  },
  rxState: function rxState() {
    return {
      auth: this.service.auth$,
      loading: this.teamSchduleService.loading$,
      cardList: this.teamSchduleService.scheduleTeamCourseList$,
      teamTemplateList: this.teamSchduleService.teamTemplateList$
    };
  },
  components: {
    Calendar: Calendar
  },
  data: function data() {
    return {
      TIME_UNIT: TIME_UNIT
    };
  },
  computed: {
    btnOptions: function btnOptions() {
      return [{
        text: '批量排期',
        click: this.onClickScheduleInBatch,
        if: this.auth['addBatch']
      }, {
        text: '复制排期',
        click: this.onClickCopySchedule,
        if: this.auth['copy']
      }, {
        text: '爽约惩罚会员',
        click: this.showPunishModal
      }, {
        text: '生成课表',
        click: this.createTimeTableModal
      }];
    },
    startDate: function startDate() {
      return this.$searchQuery.start_date || moment().format('YYYY-MM-DD');
    }
  },
  methods: {
    onMouseLeave: function onMouseLeave() {
      console.log('onMouseLeave');
    },
    // 新增团课排期
    onAddSchedule: function onAddSchedule(date) {
      var _this = this;

      console.log(date);
      this.$modalRouter.push({
        name: 'schedule-team-add-course',
        props: {
          time: date
        },
        on: {
          ok: function ok(res) {
            console.log('ok');

            _this.onScheduleChange();
          },
          onClickBatch: function onClickBatch() {
            _this.onClickScheduleInBatch();
          }
        }
      });
    },
    // 查看详情
    onDetail: function onDetail(info) {
      var _this2 = this;

      console.log(info);
      this.$modalRouter.push({
        name: 'schedule-team-reserve-info',
        props: {
          id: info.id
        },
        on: {
          ok: function ok(res) {
            _this2.onScheduleChange();
          }
        }
      });
    },
    // 批量排期(打开课程管理或者新增课程)
    onClickScheduleInBatch: function onClickScheduleInBatch() {
      var _this3 = this;

      // this.$modalRouter.push({
      //   name: 'schedule-team-add-course-batch',
      //   on: {
      //     ok: res => {
      //       this.onScheduleChange()
      //     }
      //   }
      // })
      this.teamSchduleService.getTeamTemplate().subscribe(function (res) {
        // 打开课表管理
        if (!_this3.teamTemplateList.length) {
          _this3.addOrEditCourse();

          return;
        }

        _this3.$modalRouter.push({
          name: 'schedule-batch-course-manage',
          props: {
            teamTemplateList: _this3.teamTemplateList,
            type: 'team'
          },
          on: {
            // 确定时开始删除数据
            save: function save(res) {
              _this3.teamSchduleService.delTeamTemplate({
                id: JSON.stringify(res)
              }).subscribe();
            },
            // 新增课表打开新增课表弹窗
            add: function add() {
              setTimeout(function () {
                _this3.addOrEditCourse();
              }, 500);
            },
            // 编辑课表打开编辑课表弹窗
            edit: function edit(res) {
              setTimeout(function () {
                _this3.addOrEditCourse(res);
              }, 500);
            }
          }
        });
      });
    },
    // 新增和编辑团课
    addOrEditCourse: function addOrEditCourse() {
      var _this4 = this;

      var id = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : undefined;
      this.$modalRouter.push({
        name: 'schedule-batch-add-edit-course',
        props: {
          id: id,
          type: 'team'
        },
        on: {
          success: function success(res) {
            setTimeout(function () {
              _this4.$modalRouter.push({
                name: 'schedule-batch-course-rank-preview',
                props: {
                  dataTable: res.info,
                  type: 'team'
                },
                on: {
                  success: function success(res) {
                    _this4.onScheduleChange();
                  }
                }
              });
            }, 500);
          }
        }
      });
    },
    // 复制排期
    onClickCopySchedule: function onClickCopySchedule() {
      var _this5 = this;

      this.$modalRouter.push({
        name: 'schedule-team-copy-schedule',
        on: {
          ok: function ok(res) {
            _this5.onScheduleChange();
          }
        }
      });
    },
    // 进入表格模式
    onGetTable: function onGetTable() {
      this.$router.push({
        name: 'shop-product-course-schedule-team-team-table',
        query: this.$searchQuery
      });
    },
    // 刷新页面
    onScheduleChange: function onScheduleChange() {
      this.$router.push({
        query: this.$searchQuery
      });
    },

    /**
     * 爽约惩罚列表
     */
    showPunishModal: function showPunishModal() {
      this.$modalRouter.push({
        name: 'schedule-team-punish-list'
      });
    },

    /**
     * 生成课表
     */
    createTimeTableModal: function createTimeTableModal() {
      this.$modalRouter.push({
        name: 'schedule-team-time-table'
      });
    }
  },
  watch: {
    $route: function $route(newValue, oldValue) {
      this.service.scheduleService.getList(this.$searchQuery);
      this.$refs.calendar.getWeeks(this.$searchQuery.time_unit === this.TIME_UNIT.TIME_WEEK ? 'week' : '');
    }
  }
};
