import { ruleOptions } from "./add-signup.config";
import { PatternService } from '@/services/pattern.service';
import { MessageService } from '@/services/message.service';

var uuidv1 = require('uuid/v1');

export default {
  name: 'ModalAddTicket',
  bem: {
    bModal: 'modal-marketing-add-signup'
  },
  serviceInject: function serviceInject() {
    return {
      pattern: PatternService,
      msg: MessageService
    };
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      extraTypeList: ['text', 'textarea', 'radio', 'checkbox'],
      form: form,
      decorators: decorators,
      show: false,
      ticketType: 1,
      option: '',
      extraType: 1,
      extra_info: []
    };
  },
  props: {
    modalTitle: {
      type: String,
      default: '报名信息设置'
    },
    extra_sort: Number,
    signUpList: {
      type: Array,
      default: function _default() {
        return [];
      }
    }
  },
  computed: {
    isShowSignUp: function isShowSignUp() {
      return this.extraType === 2 || this.extraType === 3;
    }
  },
  methods: {
    onCLickDelOption: function onCLickDelOption(value) {
      this.extra_info = this.extra_info.filter(function (item) {
        return item !== value;
      });
    },
    onCLickGetOption: function onCLickGetOption() {
      if (this.extra_info.length >= 10) {
        this.msg.error({
          content: '报名的选项最多新增10个选项值'
        });
        return;
      }

      if (!this.option) {
        this.msg.error({
          content: '报名选项不能为空'
        });
        return;
      }

      if (!this.pattern.CN_EN_NUM('1-15').test(this.option)) {
        this.msg.error({
          content: '请输入1~15个字符'
        });
        return;
      }

      if (!this.extra_info.includes(this.option)) {
        this.extra_info.push(this.option);
        this.option = '';
      } else {
        this.msg.error({
          content: '选项重复,请重新填写'
        });
      }
    },
    onChangeExtraType: function onChangeExtraType(e) {
      this.extraType = e.target.value;
    },
    isUniq: function isUniq(name) {
      var bool = false;
      this.signUpList.forEach(function (item) {
        bool = item.extra_name === name;
      });
      return bool;
    },
    onSubmit: function onSubmit() {
      var _this = this;

      this.form.validate().then(function (values) {
        if (_this.isUniq(values.extra_name)) {
          _this.msg.error({
            content: '标题名重复请重新填写'
          });

          return;
        }

        _this.show = false;
        var obj = {
          extra_name: values.extra_name,
          extra_require: values.extra_require,
          extra_default_value: '',
          extra_sort: _this.extra_sort,
          extra_info: _this.extra_info,
          extra_type: _this.extraTypeList[values.extra_type],
          extra_key: uuidv1(values)
        };

        _this.$emit('show', obj);

        _this.$emit('submit', obj);
      });
    }
  }
};
