import { UserService } from '@/services/user.service';
import moment from 'moment';
import { PatternService } from '@/services/pattern.service';
import { cloneDeep, remove } from 'lodash-es';
import { EditService } from "./edit.service";
import MemberCard from '@/views/biz-components/h5/pages/member-card';
import H5Container from '@/views/biz-components/h5/h5-container';
import h5mixin from '@/views/pages/brand/product/card/member/period/h5mixin';
import { MEMBER_CARD } from '@/views/biz-components/h5/pages/member-card.config';
import CardBgRadio from '@/views/biz-components/card-bg-radio/card-bg-radio';
import StEditor from '@/views/biz-components/st/editor/editor.vue';
import { IS_TRANSFER, UNIT, SELL_TYPE, TRANSFER_UNIT } from '@/constants/card/deposit';
import { ruleOptions } from "./deposit.config";
export default {
  name: 'PageShopDepositCardEdit',
  mixins: [h5mixin],
  components: {
    StEditor: StEditor,
    MemberCard: MemberCard,
    H5Container: H5Container,
    CardBgRadio: CardBgRadio
  },
  serviceInject: function serviceInject() {
    return {
      pattern: PatternService,
      addService: EditService,
      userService: UserService
    };
  },
  rxState: function rxState() {
    var user = this.userService;
    return {
      addLoading: this.addService.loading$,
      shopName: this.userService.shop$,
      cardInfo: this.addService.cardInfo$,
      cardBgList: this.addService.cardBgList$,
      unit: this.addService.unit$,
      consumerType: this.addService.consumerType$,
      consumptionRange: this.addService.consumptionRange$,
      transferUnit: this.addService.transferUnit$,
      supportSales: this.addService.supportSales$,
      sellType: this.addService.sellType$
    };
  },
  bem: {
    b: 'st-help-popover'
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      // cardData
      form: form,
      decorators: decorators,
      UNIT: UNIT,
      SELL_TYPE: SELL_TYPE,
      TRANSFER_UNIT: TRANSFER_UNIT,
      cardType: MEMBER_CARD.DEPOSIT_CARD,
      MEMBER_CARD: MEMBER_CARD,
      cardData: {
        // 卡id
        card_id: null,
        // 会员卡名称
        card_name: '',
        // 特殊说明
        special_note: '',
        // 储值金额
        card_price: null,
        // 售卖价格
        sell_price: null,
        // 期限
        num: null,
        // 期限单位
        unit: UNIT.DAY,
        // 消费类目
        card_consumer_id: [],
        // 支持售卖时间
        start_time: '',
        end_time: '',
        // 结束时间面板是否显示
        endOpen: false,
        // 卡背景
        bg_image: {
          image_id: 0,
          image_key: this.cardBgList[0].image_key,
          image_url: '',
          index: 1
        },
        // 是否配置了用户端
        appConfig: true,
        // 卡介绍
        card_contents: '',
        // 备注
        description: '',
        // 是否支持转让
        _is_transfer: false,
        is_transfer: IS_TRANSFER.NOT_ALLOW,
        // 转让单位
        transfer_unit: TRANSFER_UNIT.RMB,
        // 转让手续费
        transfer_num: undefined,
        // 售卖方式
        card_sell_type: [SELL_TYPE.OFFLINE]
      },
      // 卡背景的help文本
      cardBgValidatorText: '',
      transfer_unit_is_first: true,
      // 售卖时间
      start_time: null,
      end_time: null,
      // 是否禁止选择开始时间,如果卡状态是可售卖，则需要禁止修改开始时间
      startTimeIsDisabled: false
    };
  },
  mounted: function mounted() {
    this.init();
    this.h5CardInfo.consumption_range = {
      id: 1,
      name: this.shopName.name
    };
  },
  methods: {
    init: function init() {
      this.form.setFieldsValue({
        'cardData.special_note': this.cardInfo.special_note,
        'cardData.card_price': this.cardInfo.card_price,
        'cardData.sell_price': this.cardInfo.sell_price,
        'cardData.num': this.cardInfo.num,
        'cardData.card_consumer_id': this.cardInfo.card_consumer_id,
        start_time: moment(this.cardInfo.start_time),
        end_time: moment(this.cardInfo.end_time),
        'cardData.transfer_num': this.cardInfo.transfer_num
      }); // 储值金额

      this.cardData.card_price = this.cardInfo.card_price; // 售卖价格

      this.cardData.sell_price = this.cardInfo.sell_price; // 期限

      this.cardData.num = this.cardInfo.num;
      this.cardData.unit = this.cardInfo.unit; // 消费类目

      this.cardData.card_consumer_id = this.cardInfo.card_consumer_id; // 支持售卖时间

      this.start_time = moment(this.cardInfo.start_time);
      this.end_time = moment(this.cardInfo.end_time);
      this.startTimeIsDisabled = moment(this.cardInfo.start_time).valueOf() < moment().startOf('day').valueOf() && moment(this.cardInfo.end_time).valueOf() > moment().valueOf(); // 转让设置

      this.cardData._is_transfer = !!this.cardInfo.is_transfer;
      this.cardData.transfer_unit = this.cardInfo.transfer_unit;
      this.cardData.transfer_num = this.cardInfo.transfer_num; // 售卖方式

      this.cardData.card_sell_type = this.cardInfo.card_sell_type; // 卡背景

      this.cardData.bg_image = cloneDeep(this.cardInfo.bg_image); // 卡介绍

      this.cardData.card_contents = this.cardInfo.card_contents; // 卡备注

      this.cardData.description = this.cardInfo.description;
      this.initH5CardInfo();
    },
    // 保存
    onHandleSubmit: function onHandleSubmit(e) {
      var _this = this;

      e.preventDefault();
      this.cardBgValidator();
      this.form.validateFieldsAndScroll(function (err, values) {
        if (!err && _this.cardBgIsOk) {
          // 卡名称
          _this.cardData.card_name = values.cardData.card_name; // 特殊说明

          _this.cardData.special_note = values.cardData.special_note; // 储值金额

          _this.cardData.card_price = values.cardData.card_price; // 售卖价格

          _this.cardData.sell_price = values.cardData.sell_price; // 期限

          _this.cardData.num = values.cardData.num; // 消费类目

          _this.cardData.card_consumer_id = cloneDeep(values.cardData.card_consumer_id); // 时间

          _this.cardData.start_time = "".concat(_this.start_time.format('YYYY-MM-DD'));
          _this.cardData.end_time = "".concat(_this.end_time.format('YYYY-MM-DD')); // 卡id

          _this.cardData.card_id = +_this.$searchQuery.id;

          _this.addService.editCard(_this.cardData).subscribe(function (res) {
            _this.$router.push({
              name: 'shop-product-card-deposit-list-all'
            });
          });
        }
      });
    },
    // 售卖时间-start
    start_time_change: function start_time_change(data) {
      this.start_time = cloneDeep(data);
    },
    // 开始时间
    disabledStartDate: function disabledStartDate(startValue) {
      var endValue = this.end_time;

      if (!endValue) {
        // 结束时间未选择
        return startValue.valueOf() < moment().startOf('day').valueOf();
      }

      var start = endValue.valueOf() > moment().add(30, 'y').valueOf() ? moment(endValue).subtract(30, 'y').valueOf() : moment().startOf('day').valueOf();
      return startValue.valueOf() < start || startValue.valueOf() > moment(endValue).valueOf();
    },
    handleStartOpenChange: function handleStartOpenChange(open) {
      if (!open) {
        this.cardData.endOpen = true;
      }
    },
    // 售卖时间-end
    end_time_change: function end_time_change(data) {
      this.end_time = cloneDeep(data);
    },
    handleEndOpenChange: function handleEndOpenChange(open) {
      this.cardData.endOpen = open;
    },
    disabledEndDate: function disabledEndDate(endValue) {
      var startValue = this.start_time;

      if (!startValue) {
        // 开始时间未选择
        return endValue.valueOf() < moment().startOf('day').valueOf();
      }

      return endValue.valueOf() >= moment(startValue).add(30, 'y').valueOf() || endValue.valueOf() < moment(startValue).valueOf() || endValue.valueOf() < moment().startOf('day').valueOf();
    },
    // moment
    moment: moment,
    // 转让
    transfer: function transfer(e) {
      this.cardData._is_transfer = e.target.checked; // 重置转让费用的校验

      this.form.resetFields(['cardData.transfer_num']);
    },
    transfter_change: function transfter_change(data) {
      this.cardData.transfer_num = data;
    },
    onCardBgChange: function onCardBgChange(e) {
      this.cardBgValidatorText = '';
    },
    // 卡背景校验
    cardBgValidator: function cardBgValidator() {
      var validata = this.cardData.bg_image.image_key !== '';
      this.cardBgValidatorText = validata ? '' : '请上传卡背景';
    }
  },
  watch: {
    'cardData._is_transfer': {
      deep: true,
      handler: function handler(newVal, oldVal) {
        this.cardData.is_transfer = +newVal;
      }
    },
    'cardData.transfer_unit': {
      deep: true,
      handler: function handler() {
        if (!this.transfer_unit_is_first) {
          this.form.resetFields(['cardData.transfer_num']);
        }

        this.transfer_unit_is_first = false;
      }
    }
  },
  computed: {
    // 售卖方式
    sell_type_list: function sell_type_list() {
      var sell_type = cloneDeep(this.sellType);
      var arr = [];
      sell_type.forEach(function (i) {
        arr.push({
          value: i.value,
          label: i.label
        });
      });

      if (!this.cardData.appConfig) {
        remove(arr, function (i) {
          return i.value === SELL_TYPE.CLIENT;
        });
      }

      return arr;
    },
    // 卡背景是否校验通过
    cardBgIsOk: function cardBgIsOk() {
      return this.cardBgValidatorText === '';
    },
    // 转让设置的min
    transferMin: function transferMin() {
      return 0;
    },
    // 转让设置的max
    transferMax: function transferMax() {
      return this.cardData.transfer_unit === TRANSFER_UNIT.PERCENT ? 100 : 999999.9;
    }
  }
};
