import { cloneDeep } from 'lodash-es';
import { SmallCourseScheduleService } from '@/views/pages/shop/product/course/schedule/small-course/service#/schedule.service';
import { SmallCourseScheduleCommonService } from '@/views/pages/shop/product/course/schedule/small-course/service#/common.service';
import moment from 'moment';
import { ruleOptions } from "./add-course.config";
export default {
  name: 'AddCourseSchedule',
  serviceInject: function serviceInject() {
    return {
      smallCourseScheduleService: SmallCourseScheduleService,
      smallCourseScheduleCommonService: SmallCourseScheduleCommonService
    };
  },
  rxState: function rxState() {
    var tss = this.smallCourseScheduleCommonService;
    return {
      loading: this.smallCourseScheduleService.loading$,
      coachSmallCourseOptions: tss.coachBindOptions$,
      courseSmallCourseOptions: tss.courseSmallCourseOptions$,
      courtOptions: tss.courtOptions$
    };
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false,
      courseItem: '',
      moment: moment,
      smallCourseInfo: {},
      courseStartDate: ''
    };
  },
  props: {
    time: {
      type: Object,
      default: function _default() {
        return moment();
      }
    }
  },
  created: function created() {
    this.smallCourseScheduleCommonService.getCourseList({
      schedule_status: 1
    }).subscribe();
  },
  methods: {
    changeStartDays: function changeStartDays(valus) {
      this.courseStartDate = valus.format('YYYY-MM-DD');
    },
    onChangeCourse: function onChangeCourse(value) {
      var _this = this;

      this.smallCourseScheduleCommonService.getBindCoachList(value).subscribe();
      this.courseSmallCourseOptions.forEach(function (item, index) {
        if (item.course_id === value) {
          _this.smallCourseInfo = item;
          console.log(_this.smallCourseInfo);
        }
      });
    },
    onSubmit: function onSubmit() {
      var _this2 = this;

      this.form.validate().then(function (values) {
        var form = cloneDeep(values);
        console.log(values);
        var start_days = values.start_days.format('YYYY-MM-DD');
        var start_time = values.start_time.format('HH:mm');
        var end_time = values.end_time.format('HH:mm');
        form.start_time = start_days + ' ' + start_time;
        form.end_time = start_days + ' ' + end_time;
        form.cycle_start_date = _this2.smallCourseInfo.course_begin_time;
        form.cycle_end_date = _this2.smallCourseInfo.course_end_time;

        if (form.court_id) {
          form.court_site_id = +form.court_id[1];
          form.court_id = +form.court_id[0];
        } // 提交


        console.log(form);

        _this2.smallCourseScheduleService.add(form).subscribe(function (res) {
          if (!res.conflict) {
            _this2.$emit('ok');

            _this2.show = false;

            _this2.onScheduleChange();
          }
        });
      });
    },
    onScheduleChange: function onScheduleChange() {
      this.$router.reload();
    },
    onClick: function onClick() {
      this.show = false;
    }
  }
};
